/* ------BACKGROUND------ */
/* i really want this to be global */
body {
    background: url("https://media.discordapp.net/attachments/870656439159521293/874798547587960832/anime-sky-torii-nengoro-hd-wallpaper-preview.jpg");
    background-size: cover;
}
.appMount-3lHmkl {
    background: rgba(0, 0, 0, .6);
}

/* ------REMOVE GREY/WHITE BG COLORS------ */
.theme-dark,
.theme-light {
    --header-primary: #fff;
    --header-secondary: #b9bbbe;
    --text-normal: #dcddde;
    --text-muted: #9d9d9d;
    --channels-default: #8e9297;
    --interactive-normal: #b9bbbe;
    --interactive-hover: #dcddde;
    --interactive-active: #fff;
    --interactive-muted: #4f545c;
    --background-primary: transparent;
    --background-secondary: transparent;
    --background-secondary-alt: transparent;
    --background-tertiary: transparent;
    --background-floating: rgba(0, 0, 0, .8);
    --channeltextarea-background: transparent;
    --deprecated-panel-background: transparent;
    --background-modifier-hover: rgba(0, 0, 0, .42);
    --background-modifier-selected: rgba(0, 0, 0, .45);
}

/* ------POP-UP------ */
.footer-1fjuF6,
.headerNormal-T_seeN,
.body-3iLsc4,
