css:
@-moz-document domain("okoun.cz") {
.item .meta .user {
    color: #bbb;
}

.item .content {
    color: #ddd;
}

.item .meta {
    padding-right: 0em;
}

.head .user .login input.text {
    width: 9em;
    border: 0;
    height: 24px;
}


#body {

    margin: 0px;
    padding: 3px;
	background-color: #222;
}

body {
   padding: 0;
}

body,
.item.hover .actions span.prev {
	color: #aaa;
}

.room .item.new {
    background-color: #555;
}


a, h2 a:visited {
    color: #afc;
}
a:visited {
    color: #eee;
}

.head .menu a, .head .menu a:visited, .head .user a, .head .user a:visited, .footer a, .footer a:visited, .item.hover .actions a, .item.hover .actions span.prev a.prev {
    color: #eee;
}

.welcome, .item .content .confirm.reply, .item .actions .share:after, .item.new .actions .share:after {
     background-color: transparent;
}

.room .item.new, .room .item  {
     border: 0px; 
     border-bottom: 1px solid; 
}



.item .actions, .item.new .actions  {
    border-top: 1px solid #555;
}

html {
    background: #000;
}

.head h1 a {
     background-image: none;
}


.item .date, .item .permalink a, .item .meta a {
    color: #555;
} 


input, textarea {
    background-color: #111;
    color: #888;
}

.yui-button button {
    background-color: #555;
}

.main {
    margin-left: 10px;
}

.pager ul li, .pager ul li.older, .pager ul li.oldest, .pager ul li.newer, .pager ul li.newest, .index .news .yui-g.first .yui-u  {
    background-color: unset; 
    background-image: none;
}

.search input.text, .board .main .search input.text.authors {
    border: 0;
    height: 22px;
    padding-top: 2px;
    padding-bottom: 0px;
}

a {
    text-decoration: none;
}
.item .date, .item .permalink a, .item .meta a {
    color: #999;
}
}
