@-moz-document domain(search.brave.com) {
/* Edit this variables to change images and colors */
:root {
  --backgroundimage: url("/*[[backimagecustom]]*/");
  --backgroundsize: /*[[backsize]]*/%;
  --iconimage: url("/*[[logoimagecustom]]*/");
  --focuscolor: /*[[maincolor]]*/;
  --secondcolor: /*[[color2]]*/;
}

 /* Code */

body, .home__main {
	background-image: var(--backgroundimage);
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: var(--backgroundsize);
}

.navbar {
	opacity: 90%;
}

.suggestions.svelte-1xudttb, :root.dark .suggestions.svelte-1xudttb.svelte-1xudttb, :root.light .suggestions.svelte-1xudttb.svelte-1xudttb {
    border: 2px solid var(--focuscolor);
    border-top: none;
}

#navbar-tabs{
    border-top: solid 2px var(--focuscolor);
    border-bottom: solid 2px var(--focuscolor);
}

#nav-tabs .tab {
	margin-bottom: 5px;
}



.btn--text:hover:focus, .tab .tab-item a:focus{
    background-color: var(--secondcolor);
    
}
.infobox {
	opacity: 95%;
    border: solid 2px var(--focuscolor);
    border-radius: 10px;
}

.btn-round.svelte-c04oor.svelte-c04oor:hover {
	background-color: var(--secondcolor);
	border: solid 2px var(--focuscolor);
}

.feedback-modal.svelte-j5byrn.svelte-j5byrn {
	border-radius: 10px;
	border: solid 2px var(--focuscolor);
    opacity: 95%;
}

.slide-button.svelte-135e65j:hover {
	background-color: var(--secondcolor);
	border: solid 2px var(--focuscolor);
}

#results{
    opacity: 95%;
}

:root.dark .carousel .card, :root.light .carousel .card {
	border: solid 2px var(--focuscolor);
    border-radius: 10px;
}

.flex-wrap {
	display: flex;
	flex-wrap: wrap;
	color: #fff;
	background: #2f313b;
	border-radius: 10px;
}

.ContextMenu.svelte-6e566y.svelte-6e566y {
	border: solid 2px var(--focuscolor);

}

#search-elsewhere .chip {
	background-color: #686868;
}

*, ::after, ::before {
	color: var(--focuscolor);
    fill: var(--focuscolor);
}

.filter-name.svelte-sk0mu.svelte-sk0mu, .filter-name.svelte-1eliug2 {
	margin-right: 0px;
	color: #fff;
}


.carousel-snippet .header i, .carousel-snippet .header .icon {
	--size: 18px;
	color: var(--color-gray-2);
	margin-left: 10px;
}

.snippet {
	border: solid 2px var(--focuscolor);
	border-radius: 10px;
	margin-bottom: 10px;
}

.color-primary {
    color: #9f9f9f !important;
	border-radius: 10px;
}

#nav-tabs .tab-item a:hover{
    color: #9f9f9f !important;

}

.image-viewer.svelte-c04oor.svelte-c04oor {
	border-top: solid 2px var(--focuscolor);
	border-bottom: solid 2px var(--focuscolor);
}


#nav-logo img {
	opacity: 0;
}

.logo-img , .logo-light, .logo-large, .logo-small, .logo-dark {
	opacity: 0%;
}

#logo, #logo:focus {
    border: 2px solid var(--focuscolor);;
	background-image: var(--iconimage);
	background-size: 250px;
	background-repeat: no-repeat;
	border-radius: 10px;
	background-position-y: center;
    background-position-x: center;

}

#nav-logo {
    border: 2px solid var(--focuscolor);
    background-image: var(--iconimage);
	background-size: 100px;
	background-repeat: no-repeat;
	border-radius: 10px;
	background-position-y: center;
    background-position-x: center;
   
}

:root.dark #searchform, :root.dark, :root.light #searchform, :root.light .suggestions.svelte-1xudttb.svelte-1xudttb {
	border-color: var(--focuscolor);
}


#submit-button:hover {
	background: linear-gradient(302.53deg, var(--secondcolor) 0%, #939393 46.25%, var(--focuscolor) 100%);
}



.btn {
	margin-right: 1px;
}

.filter.svelte-1eliug2, .wrapper.svelte-sk0mu {
	margin-right: 15px;
    margin-left: 10px;
    margin-top: 3px;
    margin-bottom: 3px;
	background-color: rgba(36, 39, 49, .9);
	border-radius: 10px;
}




#wrapper.svelte-1jw358w {
    size: 30px;
	margin-right: 25px;
    margin-left: 10px;
    margin-top: 3px;
    margin-bottom: 3px;
    background-size: 25px;
    background-position: center;
    background-repeat: no-repeat;
	border-radius: 10px;
}

.btn--icon:hover:not(:focus) {
	background-color: rgba(36, 39, 49, .6);
}

.filter-name.svelte-sk0mu.svelte-sk0mu {
	margin-right: 0px;
	margin-left: 10px;
}


.filter-name.svelte-1eliug2 {
	margin-right: 0px;
	margin-left: 10px;
}

.btn svg {

	margin: 1px;
}

.btn--tiny.btn--icon svg {
	size: 30px;
    
}

.btn--filled {
	--color: var(--btn-filled-text);
	color: var(--color);
	background-color: var(--focuscolor);
	border-color: transparent !important;
}

#snippet-thumb.svelte-n72q2g img.svelte-n72q2g {
	opacity: 0;
}



#snippet-thumb.svelte-n72q2g.svelte-n72q2g {
	background-image: url("https://imgr.search.brave.com/UI4n3RnlYoTRV9NWm5kJhRiJ9xxpuD45uSTfrq_k2us/fit/540/540/no/1/aHR0cHM6Ly92aWdu/ZXR0ZS53aWtpYS5u/b2Nvb2tpZS5uZXQv/dGVuc2VpLXNoaXRh/cmEtc2xpbWUtZGF0/dGEta2VuL2ltYWdl/cy8zLzM0L1JpbXVy/dV9TbGltZV9Bbmlt/ZS5wbmcvcmV2aXNp/b24vbGF0ZXN0P2Ni/PTIwMTgwOTIyMjE0/MzA0");
	background-size: 120%;
	background-repeat: no-repeat;
	background-position: center;
}

.snippet-thumb.svelte-12rude3.svelte-12rude3 {
    content: url("https://imgr.search.brave.com/-8APEeg8HkptDL4VJ_f7X858e0PNe-Cu1RQuxzrXMbA/fit/1024/1024/no/1/aHR0cHM6Ly9nYW1l/cGVkaWEuY3Vyc2Vj/ZG4uY29tL2dyYW5k/c3VtbW9uZXJzX2dh/bWVwZWRpYV9lbi9m/L2YyL1JpbXVydV8o/U2ltZS1mb3JtKV9G/dWxsX0FydC5wbmc_/dmVyc2lvbj0yMmFm/YzY2Y2E5MjgwMDc3/MTNlNzc2OTBjMDQ1/YTRlMA");
}

.btn--filled {
	border-bottom: solid 2px var(--focuscolor);
}

.btn--filled:hover:not(:focus) {
	background-color: var(--secondcolor);
	box-shadow: none;
}

.btn--filled:active, .btn:focus {
		background-color: var(--secondcolor);
}

.text-gray, .carousel-snippet .carousel .card .card-footer__timestamp {
	color: var(--focuscolor) !important;
}

.card {
	border: solid 2px var(--focuscolor);
	border-radius: 10px;
	margin-bottom: 10px;
    margin-right: 10px;
}

.btn.svelte-qxmq3z.svelte-qxmq3z {
	background-color:  var(--focuscolor);
}

.color-primary.title {
	background: #35394800;
}

footer, .footer {
	opacity: 90%;
    border-top: solid 2px var(--focuscolor);
}

.btn:focus {
	box-shadow: 0 0 0 2px var(--focuscolor);
}

.btn.svelte-jms1j4 .icon, .btn--sm.btn--icon svg {
	margin: 5px;
}

a:focus {
	box-shadow: 0 0 0 .1rem var(--focuscolor);
	border-radius: 10px;
}

.btn--icon:hover:not(:focus) svg, .btn--icon:active svg {
	fill: var(--focuscolor);
}

.btn--text:hover, .btn--text:active, .btn--text:hover:not(:focus), .tab .tab-item a:focus, .tab .tab-item a:hover  {
	color: var(--focuscolor);
}


.btn--outline:active, .btn--outline:hover:not(:focus) {
	border: 1px solid var(--focuscolor);
	box-shadow: 0 0 0 1px var(--focuscolor);
	color: var(--focuscolor);
}

.form-checkbox input:checked + .form-icon, .form-radio input:checked + .form-icon, .form-switch input:checked + .form-icon {
	background: var(--focuscolor);
	border-color: var(--focuscolor);
}

.img-wrapper.svelte-y42u0h {
	border: solid 1px #28282800;
    border-radius: 10px;
}

.image.svelte-y42u0h {
	border-radius: 10px;
}

.selected.svelte-y42u0h {
	border: none;
	box-shadow: 0 0 0 2px var(--focuscolor);
	border-color: var(--focuscolor);
}
}
