css:
@-moz-document domain("derstandard.at"){
	/* neutral colors */
	div, body { background-color: #fff !important; }
	div, header { background-color: #fff !important; }


  /* clean up article list view */
  section:not([data-type="date"]){display:none!important; }
  picture { display:none!important;}
  .teaser-postingcount { display:none!important;}
  .js-article-tool.story-tool {display:none!important;}
  section[data-type="date"] article {
      --tile-padding: unset !important;
  }
  section.ad-containers-chronological, .overview-readmore, .overview-nomore, section[data-type="date"], .layout {
    grid-column-gap:unset!important;
  grid-row-gap: unset!important;
  grid-template-columns:unset!important;
      max-width:80%;
  }


  /* clean up navbar */
  .brand { display:none!important;}
  nav.path { display:none!important;}
  nav.meta  { display:none!important;}
  div.login { display:none!important;}
  div.site-guide-toggle {display:none!important;}
  form.search {display:none!important;}
  .site--header-next > div {
      min-height: unset;
      max-height:50px!important;
  }
  .site--header-next {
      max-height:50px!important;
      min-height:unset!important;
  }
  body{ padding-top: 12px; }

  /* misc */

  .site-footer {display:none!important;}
  .tp-container-inner {display:none!important;}
}
