@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.winamp.com") {

	body {
		background: #080808 !important;
		font-family: verdana, arial, helvetica, sans-serif !important;
		color: #fff !important;
		overflow-y: scroll;
	}

	a { 
		color: #fff !important; 
		text-decoration: none !important;
	}
	a:hover {
		text-decoration: underline !important;
		text-shadow: 0 0 2px #fff;
	}
div[id*="post_message_"] a { text-decoration: underline !important; }

	.bigusername, tbody tr td + td[align="left"] div a, tbody tr td table tr td + td + td div a, td + td div div span a, td div + div + div + div + div div a, tr td + td[id^="td_threadtitle_"] div > a, .thead a { color: #ffbf06 !important; }
	.bigusername:hover, tbody tr td + td[align="left"] div a:hover, tbody tr td table tr td + td + td div a:hover, td + td div div span a:hover, td div + div + div + div + div div a:hover, tr td + td[id^="td_threadtitle_"] div > a:hover, .thead a:hover {
		text-decoration: underline !important;
		text-shadow: 0 0 2px #ffbf06 !important;
	}

	#GH_nav li ul li a {
		color: #000 !important;
	}
	#GH_nav li ul li a {
		text-shadow: none !important;
	}

	table, td, tr, div {
		border: 0 !important;
	}

	hr[noshade="noshade"], blockquote pre hr, hr[size="1"] { color:#101010 !important; background-color: #101010 !important; height: 2px !important; border: 0; border-bottom: 1px solid #202020;}
	td.thead { font-weight: bold !important; font-size: x-small !important; color: #ffbf08 !important; background-color: #282828 !important; }

	#footerbtm, #footerLinks, #bannerAd, .bannerAd, #GH_hat, #GH_guts, table[width="982"] {display: none !important;}

	.GH_Footer{display: none !important;}
	.vb_copyright{display: none !important;}

	div.container{ width: 95% !important;}
	div.page{ width: 95% !important;}

	#GH_{ width: 95% !important;}
	#GH_ #GH_nav{ width: 99% !important;}
	#GH_  #GH_hat{ width: 99% !important;}
	#GH_ #GH_guts{ width: 100% !important;}
	#GH_nav { background: #101010 !important; height: 15px !important; padding-top: 10px !important; }
	#GH_nav_download {display: none !important;}
	#GH_nav_act_B { color: #000 !important; }

	pre { white-space: pre-line !important;  line-height: 0.95em;}

	div:not([align="center"]) div table:not([align="center"])[cellpadding="6"] tr td.alt2:not([width="175"]) {
		background: #101010 !important;
		border: 1px solid #171717 !important;
		font-style: normal !important;
		color: #ededed !important;
		margin: 5px 0px 5px 10px !important;
		padding: 5px !important;
		width: 95% !important;
		box-shadow: black 1px 1px 5px;
	}

	td div + div + div + div + div {
		background-color: #101010 !important;
		color: #fff !important;
		border: 1px solid #282828 !important;
		box-shadow: inset black 1px 1px 10px;
	}

	.alt2[width="175"], tr td.alt2, .alt1, .alt1Active, .thead, .tcat, .tfoot, .panelsurround, input:not([type="checkbox"]):not([type="image"]) { background: #202020 url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAPCAYAAAAlH6X5AAAAKklEQVR42mNgYGAIZgIS31CJ73DiB4j4xQwkWEEEM4hgAhGMIIIBTIABAHVqB0HbaakVAAAAAElFTkSuQmCC') repeat-x !important; }
	.alt2[width="175"], tr td.alt2, .alt1, .alt1Active { color: #fff !important; }

	.tcat { font-weight: bold !important; color: #ffbf08 !important; }

	* { border-spacing: 0 !important; }

	input:not([type="checkbox"]):not([type="radio"]), textarea, select, option, iframe { -moz-appearance: none !important; /* Magic FF override! */ }
	select {
		background-color: #101010 !important;
		color: #fff !important;
		border: 1px solid #202020 !important;
		padding: 5px !important;
 		box-shadow: inset black 1px 1px 10px;
	}
	option {
		background-color: #101010 !important;
		color: #fff !important;
	}
	option[selected="selected"] {
		color: #ffbf06 !important;
		font-weight: bold !important;
	}
	textarea, iframe, input[type="text"], input[type="password"] {
		background-color: #101010 !important;
		color: #fff !important;
		border: 1px solid #282828 !important;
		padding: 5px !important;
		box-shadow: inset black 1px 1px 10px;
	}
	input[type="text"], input[type="password"] {
		box-shadow: inset black 1px 1px 5px;
	}
	input[type="text"]:hover, input[type="password"]:hover, textarea:hover, iframe:hover {
		border: 1px solid #303030 !important;
	}
	input:not([type="checkbox"]):not([type="image"]):not([type="text"]):not([type="password"]) {
		font-weight: bold !important;
		cursor: pointer !important;
		background-color: #181818 !important;
		color: #fff !important;
		border: 1px solid #202020 !important;
		padding: 5px !important;
		text-shadow: 1px 1px 2px #000, 1px 1px 5px #000 !important;
    	box-shadow: black 1px 1px 5px;
	}
	input:not([type="checkbox"]):hover:not([type="image"]):not([type="text"]):not([type="password"]) {
		background-color: #202020 !important;
		border-color: #282828 !important;
	}
	input:not([type="checkbox"]):active:not([type="image"]):not([type="text"]):not([type="password"]) {
		background-color: #101010 !important;
		border-color: #181818 !important;
	}
	fieldset { border: 0 !important; color: #fff !important; }
	legend { color: #fff !important; }
	#collapseobj_quickreply .panelsurround .smallfont { font-weight: bold !important; }
	/* Lets not theme checkboxes, for now. */
	/*input[type="checkbox"] { 
		border: 1px solid #202020 !important;
		color: #fff !important;
		background-color: #181818 !important;
	}*/
	/* Okay so Radio button theming did work... but for some reason it's not now.. what did I change?
	input[type="radio"]:hover {
		background-color: #303030 !important;
		border: 1px solid #ffbf06 !important;
	}
	input[type="radio"]:checked {
		background-color: #805a00 !important;
		border: 1px solid #ffbf06 !important;
	}*/

	.alt1, .alt1Active { background-color: #181818 !important;}

	.bigusername {text-decoration: none !important;}

	.page { background-color: #101010 !important; color: #fff !important;}

	.vbmenu_popup { background-color: transparent !important; margin: -10px;}
	.vbmenu_popup table {
		margin: 10px !important;
		border: 1px solid #202020 !important;
		box-shadow: black 1px 1px 5px !important;
	}
	.vbmenu_option {
		background: #202020 !important;
		color: #fff !important;
	}
	.vbmenu_hilite {
