	}

	.tborder {
		margin: 5px 0 5px 0 !important;
		border: 1px solid #202020 !important;
		box-shadow: black 1px 1px 5px;
	}
	.controlbar, table.vBulletin_editor_table, .panelsurround { background-color: #181818 !important; color: #fff !important;}
	.panel { background: transparent !important; color: #fff !important; }
	.time { color: #fff !important; }
	tbody tr td + td[align="left"] div + div, td + td + td div + div { color: #aaa !important; }

	#content, #content_container, #activity_info, div[id="stats_mini"] div {background: transparent !important; }
	div h4 a + a + span, ul li[id*="_tab"] {color: #ffbf06 !important; }


	.imagebutton { background: #181818 !important; border: 1px solid #202020 !important; padding: 0 !important; margin: 0 !important;}
	.imagebutton:hover { background: #202020 !important; border: 1px solid #303030 !important; }
	#vB_Editor_QR_controls img[src="images/editor/separator.gif"] { display: none !important; }
	.wysiwyg {background: transparent !important; font-family: verdana, arial, helvetica, sans-serif !important; margin: 0 !important;}


