css:
@-moz-document domain("4clubbers.com.pl") 
{
body {
    background: #000000 url(https://i.imgur.com/aOtiaAl.png) no-repeat top;
}
.tcat {
    background: #107ef5;
    color: #ffffff;
    border-bottom: 5px solid #094c94;
}
.vbmenu_control {
    background: #375a76 url(https://i.imgur.com/qkebsO7.png) repeat-x top left;
}

.alt1 a:link, .alt1_alink, .alt1Active a:link, .alt1Active_alink {
  color: #ffffff;}
.page {
  background: #524b4b;
  color: #ffffff;}
.alt2, .alt2Active {
  background: #6d6d6d;
  color: #0018ff;}
.alt1, .alt1Active {
    background: #6d6d6d;
    color: #ffffff;}
.thead {
    background: #0094fb;} 

/* Odpowiedź */

.panel {
    color: #0000ff;}
.panelsurround {
    background: #959697;}
.button {
    background: #375a76 url(https://i.imgur.com/qkebsO7.png) repeat-x top left;
    color: #ffffff;}


/* Wyszukiwarka */
.vbmenu_popup {
    background: #cccccc;}

/* Profil */

.shade, a.shade:link, a.shade:visited {
    color: #ffffff;}
}
