css:
@-moz-document url-prefix("https://meet.jit.si/HackearLaPandemia")
{
/* GLOBALES */
  videoconference_page .subject .subject-text
  {
    font-family: 'Bungee Inline', cursive;
    font-size: 2em;
    line-height: 1;
    padding-top: .125em;
  }

  #videoconference_page .subject .participants-count
  {
    margin-top: -.25em !important;
  }

  #videoconference_page #videospace #largeVideoContainer
  {
    background: #36183a !important;
  }

  .watermark
  {
    display: none !important;
  }

  .display-avatar-only .displayNameContainer
  {
    visibility: visible !important;
    background: none !important;
  }

/* VISTA DE MINIATURA */
  .tile-view .active-speaker
  {
    box-shadow: 0 0 5px 3px #feb40255, 0 .125em 5em 1em #ff00ffaa;
    border: solid 3px #feb402 !important;
    transform: scale(1.25);
    z-index: 1000;
    transition: all 1s;
  }

  .tile-view #filmstripRemoteVideosContainer
  {
    width: 70vw !important;
    margin-left: 30vw !important;
  }

  .tile-view #filmstripRemoteVideosContainer .videocontainer
  {
    width: 8vw !important;
    min-width: 9vw !important;
    max-height: 8vw !important;
    min-height: 8vw !important;
  }

  .tile-view #filmstripRemoteVideosContainer .videocontainer .videocontainer__background
  {
    background: rgba(0, 0, 0, 0);
    transition: all 1s;
  }

  .tile-view #filmstripRemoteVideosContainer .videocontainer:hover .videocontainer__background
  {
    background: rgba(0, 0, 0, .4);
  }

  .tile-view #filmstripRemoteVideosContainer .videocontainer .avatar-container
  {
    width: 5em !important;
    height: 5em !important;
  }

  .tile-view #filmstripRemoteVideosContainer .videocontainer .videocontainer__toptoolbar,
.tile-view #filmstripRemoteVideosContainer .videocontainer .videocontainer__toolbar,
.tile-view #filmstripRemoteVideosContainer .videocontainer .remotevideomenu
  {
    opacity: .1;
  }

  .tile-view #filmstripRemoteVideosContainer .videocontainer:hover .videocontainer__toptoolbar,
.tile-view #filmstripRemoteVideosContainer .videocontainer:hover .videocontainer__toolbar,
.tile-view #filmstripRemoteVideosContainer .videocontainer:hover .remotevideomenu
  {
    transition: all .5s;
    opacity: 1;
  }

  .tile-view #filmstripRemoteVideosContainer .videocontainer .videocontainer__hoverOverlay
  {
    display: none;
  }

  .tile-view .avatar-svg,
.filmstrip .avatar-svg
  {
    display: none !important;
  }

  .tile-view #filmstripRemoteVideos
  {
    margin: 8vh 0;
    height: calc(100vh - 16vh);
  }

  .tile-view .videocontainer .displayname
  {
    text-shadow: 0 2px 8px black;
    left: 8%;
    top: 48%;
    text-align: center;
  }
}
