css:
@-moz-document url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com")
{
  /* yuh */

/*ad remover*/
  .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard
  {
    display: none !important;
  }
 /*header*/
  #header
  {
    background-image: url(https://i.imgur.com/g8gP5Ch.png) !important;
                    }


  .navigation .notification-icon, .rbx-highlight
  {
    background: #FFFFFF;
  }

#navigation {
         background: transparent !important;
}.light-theme .rbx-left-col {
    box-shadow: 0 0 5px 0 rgb 5d054847;
}

  #chat-header
  {
    background: #ECEBEC;
      /*Party header*/
    ;
  }

  .slide-item-container-left, .profile-avatar-right
  {
    background: #ECEBEC;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading, .rbx-tabs-horizontal .rbx-tab-heading:hover, .rbx-tabs-horizontal .rbx-tab-heading:focus, .rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 .6 #ECEBEC inset;
  }

  .forum-table-header, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th, #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #ECEBEC !important;
      /*Forum colors and messaging underline color*/
    ;
  }


  #TopAbpContainer, .ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer, #Skyscraper-Adp-Right, #Skyscraper-Adp-Left, .abp-spacer, #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even, #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd, #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even, #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd, .sponsored-game, .alert-info, #mCSB_1_container > ul > li.font-bold.small, #mCSB_1_container > ul > li.rbx-nav-sponsor
  {
    display: none !important;
  }

  #navbar-search-input, #navbar-search-btn
  {
    border: 1px solid #ECEBEC;
      /*Search border color*/
    ;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }
.light-theme .section-content
{
  background: transparent !important;
}
.light-theme .profile-avatar-right .profile-avatar-mask {
    background: transparent !important;
}

/* avatar background*/
.light-theme .thumbnail-2d-container {
    border: 0;
    background-color: rgb(0 0 0 / 0%);
    border-color: #b2017c;
}
.light-theme .avatar-container .avatar-card-image, .light-theme .avatar-container img {
    background-color: #cf0e80;
}
.light-theme .avatar .avatar-card-image {
    background-color: #cf0e80;
}

/* game server list*/
.light-theme .stack .stack-list .stack-row {
    background-color: #ffafee87;
    color: #000000;
}.dark-theme .touch .btn-common-play-game-lg.active, .dark-theme .touch .btn-common-play-game-lg:active, .dark-theme .touch .btn-common-play-game-lg:focus, .dark-theme .touch .btn-common-play-game-lg:hover, .dark-theme .touch .btn-common-play-game-lg:visited, .light-theme .touch .btn-common-play-game-lg.active, .light-theme .touch .btn-common-play-game-lg:active, .light-theme .touch .btn-common-play-game-lg:focus, .light-theme .touch .btn-common-play-game-lg:hover, .light-theme .touch .btn-common-play-game-lg:visited, .light-theme a.btn-common-play-game-lg {
    color: #fff;
    background-color: #fff7fe87;
    border-color: #8c3f7a;
}.dark-theme .touch a.btn-common-play-game-lg.active, .dark-theme .touch a.btn-common-play-game-lg:active, .dark-theme .touch a.btn-common-play-game-lg:focus, .dark-theme .touch a.btn-common-play-game-lg:hover, .dark-theme .touch a.btn-common-play-game-lg:visited, .light-theme .touch a.btn-common-play-game-lg.active, .light-theme .touch a.btn-common-play-game-lg:active, .light-theme .touch a.btn-common-play-game-lg:focus, .light-theme .touch a.btn-common-play-game-lg:hover, .light-theme .touch a.btn-common-play-game-lg:visited {
    color: #fff0;
    background-color: #ff9edd7d;
    border-color: #763466;
}

/*play button*/
.light-theme a.btn-common-play-game-lg {
    border-radius: 200px;
}


/* other game page stuff*/
.light-theme .rbx-tabs-horizontal .nav-tabs {
    background-color: #ffffff69;
}

/* profile stuff*/

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://i.imgur.com/g8gP5Ch.png) !important;
      /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

 


/* DARK THEME ? */
.dark-theme .section-content {
    background-color: #393b3d00;
    color: #fff;
}.dark-theme .profile-avatar-right .profile-avatar-mask {
    background-color: #393b3d00;
    color: #fff;
}.dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img {
    background-color: #e30d46;
}.dark-theme .avatar .avatar-card-image {
    background-color: #e30d46;
}.dark-theme .thumbnail-2d-container {
    border: 0;
    background-color: rgb(57 59 61 / 0%);
    border-color: #393b3d;
}.btr-profile .btr-games-list .btr-game-button {
    position: relative;
    padding: 4px 12px;
    background-color: #ffffff63;
    border: 1px solid #848484;
    cursor: pointer;
    font-weight: 400;
    z-index: 2;
    user-select: none;
    -moz-user-select: none;
}.dark-theme.btr-profile .btr-games-list .btr-game-button {
    background-color: #00000042;
    border-color: #741c66;
}
}
