css:
@-moz-document domain(cs-manager.com) {

body {
    background: #121212 url(http://www.forkingorcountry.com/DarK/bg1.gif) repeat !important;
}

body, td, th {
    color: #fff !important;
}

#header {
   margin: 0 auto;
   border: 0 !important;
   background: transparent url(http://www.forkingorcountry.com/DarK/logo.png) no-repeat scroll 10% center !important;
}

#header a img {
   visibility: hidden !important;
}

ul#tabtop {
   padding: 6px 0 0 0 !important;
   margin: 0 !important;
}

ul#tabtop li a, ul#tabtop li a.logout {
   padding: 5px 14px !important;
   border: 1px solid #808080 !important;
   border-top: 0 !important;
   background: #5c5c5c !important;
   opacity: 0.6;
   color: #fff !important;
}

#container {
   margin: 0 auto !important;
   background: transparent !important;
   border-right: none !important;
}

#menu, #info, #footer_left, #footer_right {
   background: transparent !important;
   height: auto !important;
   border-left: 1px solid #000 !important;
   color: #fff;  
}

/** Menu Headers **/
table.login td.head, table.rank td.head, table.dev td.head, table.table td.foot, table.data td.head, table.staff td.head, table.mail td.head, table.calender td.head, h3, h2 {
    border-top: 1px solid #303030 !important;
    border-bottom: 1px solid #121212 !important;
    background: #202020 url(http://www.forkingorcountry.com/DarK/menu_header.gif) repeat-x !important;
}

h3, h2 {
    border: 1px solid #303030 !important;
}

/** Left menu links **/
ul.menu li a, ul.gameinfo li a{
   border-bottom: 1px solid #212121 !important;
   background: #5c5c5c !important;
   opacity: 0.6;
}

table.login, table.rank {
   border: 1px solid #000 !important;
   background: #5c5c5c !important;
   opacity: 0.6;
   margin-bottom: 0 !important;
}

input.browse, input.buttonsmall, input.button {
   border: 1px solid #000 !important;
   background: #404040 !important;
   color: #000 !important;
}

div.space {
   height: 0 !important;
}

form#search {
   background: #5c5c5c !important;
   opacity: 0.6;
}

#nav {
   background: transparent !important;
   border-color: #808080 !important;
}

#path {
   color: #fff;
}

#clock {
   color: #fff;
}

div.hr {
   border-color: #808080 !important;
}

#upcoming {
   color: #fff !important;
}

 /** Links **/
#content a:link, #content a:visited, #content a:active, #footer a:link, #footer a:visited, #nav a:link, #nav a:visited, #nav a:active {
   color: #efad41 !important;
}

#tab_container {
   padding-bottom: 0 !important;
   background: transparent !important;
}

ul#tab {
   border-bottom: 0 !important;
   padding: 6px 9px 5px !important;
}

ul#tab li a {
   color: #5c5c5c !important;
}

ul#tab li span, ul#tab li {
   background: #5c5c5c !important;
   opacity: 0.6;
   color: #fff !important;
}

ul#tab li a#help_tab {
   background: #808080 !important;
   color: #fff !important;
}

#content_fixed {
   background: #404040;
   border: 1px solid #000;
}

#content {
   background: transparent !important; 
   padding-top: 0 !important;
}

table.dev td {
   background: transparent !important;
   border: 1px solid #373737 !important;
   color: #fff !important;
}

table.dev tbody tr td.value table.inline tbody tr td input {
   background-color: #404040 !important;
   color: #404040 !important;
   border-width: 0 !important;
}

table.lineup td.head {
   border-top: 1px solid #303030 !important;
   border-bottom: 1px solid #121212 !important;
   background: #202020 url(http://www.forkingorcountry.com/DarK/menu_header.gif) repeat-x !important;
}

table.lineup td.info{
   background: #5c5c5c !important;
}

table.lineup td.position, table.lineup td.player, a.lineup, table.lineup td.space {
   background-color: #404040 !important;
}

table.lineup td {
   border: 1px solid #373737 !important;
}

div.players {
   border-left: 1px solid #373737 !important;
}

ul#players li a {
   background: #404040 !important;
   border: 1px solid #373737 !important;
}

table.info {
   background: #484848 !important;
   border: 1px solid #5c5c5c !important;
}

div#training_map {
   background: #373737 !important;
   border: 1px solid #5c5c5c !important;
   border-left: 1px solid #373737 !important;
}

div#training_player {
   background: #373737 !important;
   border: none !important;
   padding-left: 3px !important;
}

div#training_type {
   background: #373737 !important;
   border: 1px solid #5c5c5c !important;
   padding-left: 2px !important;
   border-left: 0 !important;
}

div#training_players {
   background: #373737 !important;
   border-left: 1px solid #373737 !important;
   border-bottom: 1px solid #373737 !important;
}

table.table {
   border: 1px solid #373737 !important;
}

table.table tbody tr td.foot * {
   background-color: transparent !important;
}

table.table tbody tr * {
   background: #404040  !important;
}

table.table tbody tr.promotion *, table.table tbody tr.promotion_line *, table.table tbody tr.relegation *, table.table tbody tr.relegation_line * {
   background: #5d5d5d  !important;
}

table.table tbody tr.qualup *, table.table tbody tr.qualup_line *, table.table tbody tr.qualdown *, table.table tbody tr.qualdown_line * {
   background: #4f4f4f  !important;
}

div.post {
   width: 580px !important;
   border: 1px solid #373737 !important;
   background: #5c5c5c !important;
   margin: 6px 0 6px 2px !important;
}

h6 {
   border: 1px solid #373737 !important;
   background: #5c5c5c !important;
}

table.data td.value {
   background: #5c5c5c !important;
}

table.data td {
   border: 1px solid #373737 !important;
}

table.data td.foot {
   background: #5c5c5c !important;
}

table.data td.info {
   background: #5c5c5c !important;   
}

#info {
   border-right: 1px solid #000 !important;
}

div.service {
   background: #5c5c5c !important;
   border: 1px solid #121212 !important;
}

div.service div.link a {
   background: #474747 !important;
   border: 1px solid #121212 !important;
}

ul.deposit li a {
   background: #5c5c5c !important;
   border: 1px solid #474747 !important;
}

form#search input, form#search select {
   background: #474747 !important;
   color: #fff !important;
}

form#search input.button {
   color: #000 !important; 
}

input.textarea {
   background-color: #5c5c5c !important;
}

#footer_container {
   margin: 0 auto !important;
   border: none !important;
   background: transparent !important;
   color: #fff !important;
}

table.dev tbody tr td.value input {
   color: #fff !important;
}

form table.dev tbody tr td.value input {
   color: #000 !important;
}

table.dev tbody tr td.value input.button {
   color: #404040 !important;
}

table.dev tbody tr td.value div.fileinputs input {
   color: #404040 !important;
}

table.dev tbody tr td.value input.buttonsmall {
   color: #404040 !important;
}

table.staff td, table.staff td.space, table.staff td.pic {
   background: transparent !important;
}

table.staff td {
   border: 1px solid #373737 !important;
}

/**Forum**/
tr.even {
   background: #404040 !important;
}

tr.odd {
   background: #484848 !important;
}

table.dev td.info {
   background: transparent !important;
}

table.popup td {
  color: #000 !important;
}

div#content form table.info {
   background: #484848 !important;
   border: 1px solid #5c5c5c !important;
}

table.mail td {
   border: 1px solid #373737 !important;
}

table.mail tbody tr.head *, table.mail tbody tr * {
   background: #404040 !important;
}

table.calender td, table.calender td.info, div.challenge, div.disabled, a.challenge {
   background: #404040 !important;
   border: 1px solid #474747 !important;
}

div.modify {
   background: #505050 !important;
   border: 1px solid #474747 !important;
}

#nav #info {
  border: none !important;
}

}
