css:
@-moz-document domain(darakchi.uz) {
html body div.fixed-articles.slick-initialized.slick-slider.slick-dotted {
    display: none!important;
}
body > div:nth-child(2) > div:nth-child(1) {
    display: none!important;
}
body > div:nth-child(2) > div:nth-child(2) {
    display: none!important;
}
.col-xl-4 > div:nth-child(2) > a:nth-child(1) > img:nth-child(1) {
    display:none !important;
}
html body div.container.pt-5 div.row div.col-xl-9.mb-3{
width:100%!important;
}

/*Sidebar*/
 .article-as-photo > a:nth-child(1) > img:nth-child(1) {
    display:none !important;
}
.col-xl-4 > div:nth-child(3) {
    display:none !important;
}
.col-xl-4 > div:nth-child(1) {
    display:none !important;
}
.card, .article-tmpl-3 .card-body, .btn-light {
    background:#2c303a!important;
}
html body div.container.pt-5 div.row div.col-xl-3.mb-3{
display:none!important;
}

/*Foter*/
 .footer-box > div:nth-child(1) > div:nth-child(1) > div:nth-child(2),.footer-box {
    display:none !important;
}
/*Color*/
 .app-header__search > input {
    border: 2px solid #ccc!important;
}
body {
    color: #eee!important;
    background: #121212!important;
}
body a {
    color: #eee!important;
    /*color: #ffdd40!important;*/
}
.news-feed {
    border: 1px solid #000;
    padding: 6px;
    background:#2c303a;
    border-radius:5px;
}
.pg-home .news-feed__dt,.text-muted,.popular-articles__it-dt {
    color:#ffdd40!important;
}
.last-article > div:nth-child(2) {
    background:#2c303a!important;
}
.last-article > div:nth-child(2) a {
    color:#eee!important;
}
/*form*/
 .form-control {
    background:#2c303a!important;
    color:#eee!important;
}
.btn-danger, .page-link {
    background:#ffdd40!important;
    border: 2px solid #121212 !important;
    color:#121212!important;
}
/*Menu*/
.app-header__more-items{
	background:#2c303a!important;
}
}
