css:
@-moz-document domain("unicen.edu.ar") {
body{
	font-size: .79375rem;
}

#nav-drawer{
	font-size: 12px !important;
    width: 230px !important;
    top: 50px;
	height: calc(100% - 50px - 50px);
}

body.drawer-open-left #nav-drawer-footer {
    width: 230px;
    font-size: 11px;
}

nav.navbar .drawer-toggle {
    padding: 9px 11px;
}

#nav-drawer.closed{
    width: 70px !important;; 
}

body.drawer-open-left {
    margin-left: 220px;
}

.navbar {
    max-height: 50px;
}

nav.navbar {
    min-height: 50px;
}

nav.navbar .drawer-toggle {
    height: 50px;
    width: 70px;
}

nav.navbar .navbar-brand .logo img {
    max-height: 45px;
}

[data-region="right-hand-drawer"].drawer {
    width: 750px;
}

}
