/* Change the opacity when not hovered over (Lower) */
a#__ghosteryfirefox
{
  opacity: .5 !important;
}

/* Change the opacity when hovered over (Higher) */
a#__ghosteryfirefox:hover
{
  opacity: .9 !important;
}
