css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

body, input, textarea, select, option, button, th, td {
	font-family: Tahoma !important;
	font-size: 12px !important;
}

body {
	background: #000 url(http://friendfeed-media.com/f0d3a99a61e81f8d2df2d9254d18b070f54a74ba) repeat-x top !important;
	cursor: default !important;
	color: #fff !important;
}

a {
	text-decoration: none !important;
	color: #01c6f7 !important;
}

a:hover {
	color: #007c9b !important;
}

#loadingspinner {
	color: #000 !important;
	direction: ltr !important;
}

#body h1, #body p {
	color: #fff !important;
}

.subhead img {
	background-color: #fff !important;
	padding: 5px !important;
	-moz-border-radius: 8px !important;
}

.header {
	border: none !important;
	background-color: #191919 !important;
	-moz-border-radius: 8px !important;
	padding: 5px !important;
}

.header .stats {
	font-size: 11px !important;
	line-height: 140% !important;
}

.stats:hover a {
	color: #fff !important;
}

.stats a:hover .num {
	background-color: #be3f00 !important;
	-moz-border-radius: 3px !important;
}

.header h1 {
	font-family: Arial !important;
	font-weight: bold !important;
	font-size: 23px !important;
	margin-top: 10px !important;
}

.header h1 a {
	color: #fff !important;
	background-color: #be3f00 !important;
	padding: 3px !important;
	-moz-border-radius: 5px !important;
}

.servicespreview a img .icon {
	border: 1px solid #000 !important;
}

#container {
	background-color: #181818 !important;
	color: #ccc !important;
	border: 3px solid #0f0f0f !important;
}

.box-top, .box-bottom {
	background: none !important;
	display: none !important;
}

.body h1 {
	font: bold 23px Arial !important;
}

.body .l_editprofile {
	font: 11px Tahoma !important;
}

.subscribebar a, .subscribebar a:hover, .box-bar-text a, .box-bar-text a:hover {
	color: #fff !important;
}

.subbar {
	color: #333 !important;
}

.box-body {
	padding-top: 30px !important;
	background-color: #222 !important;
	margin-bottom: 35px !important;
	border: 4px solid #0f0f0f !important;
	-moz-border-radius: 8px !important;
}

#alertmessage #infomessage {
	color: #333 !important;
}

.box-body h1 {
	font: bold 23px Arial !important;
}

.box-body h2 {
	font: bold 19px Arial !important;
}

.box-body h3 {
	font: bold 17px Arial !important;
}

.description {
	direction: rtl !important;
	text-align: left !important;
}

.category h4 {
	color: #333 !important;
}

.l_tolist {
	border: 1px solid #222 !important;
	color: #333 !important;
}

.l_tolist input {
	margin-right: 10px !important;
}

.l_tolist .l_tocard {
	font-size: 12px !important;
}

.sharebox {
	border: none !important;
}

.sharebox .textbox {
	border: 2px solid #111 !important;
	background-color: #323232 !important;
	background-image: none !important;
}

.sharebox .textbox textarea {
	color: #eee !important;
	direction: rtl !important;
	background: #323232 url(http://friendfeed-media.com/52abe5319bd0285a2d85b1fa8448d369a2edcdaa) no-repeat center !important;
}

.sharebox .textbox textarea:focus {
	background-image: none !important;
}

embed#fileuploader {
	display: none !important;
}

.pager {
	border: none !important;
}

.l_profile img {
	border: 1px solid #000 !important;
}

.entry:hover .l_profile img {
	border: 1px solid #a1310a !important;
}

.entry {
	background-color: #3c3c3c !important;
	border-top: 3px solid #181818 !important;
	border-bottom: 3px solid #181818 !important;
	border-left: 2px solid #181818 !important;
	border-right: 2px solid #181818 !important;
	padding: 10px !important;
	-moz-border-radius: 8px !important;
	margin-bottom: 35px !important;
	direction: rtl !important;
}

.entry .title .name a.l_profile {
	font: bold 12px Tahoma !important;
}

.title .name:after {content: 'Ù…ÛŒâ€ŒÚ¯Ù‡: ' !important}

.entry .body .title .text {
	font: 13px Tahoma !important;
	color: #fff !important;
	margin-bottom: 15px !important;
	text-align: justify !important;
	line-height: 170% !important;
}

.thumbnail {
	border: 1px solid #000 !important;
	background-color: #fff !important;
}

.body .info {
	font: 11px Tahoma !important;
	color: #ddd !important;
	direction: ltr !important;
}

.entry.direct .info {
background-image:url(http://i.friendfeed.com/c326c3df6d1d19ebdaaa5e7583dc5d177b255b16) !important;
}

.entry .likes {
	margin-right: 0px !important;
	font-size: 12px !important;
	color: #ddd !important;
	direction: ltr !important;
}

.popupmenu {
	background-color: #333 !important;
	border: 1px solid #222 !important;
}

#feed .comments {
	font: 12px Tahoma !important;
    background-color: #343434 !important;
	padding: 5px !important;
	-moz-border-radius: 5px !important;
	margin: 10px 5px !important;
}

.entry .comment {
	font: 12px Tahoma !important;
	line-height: 180% !important;
	background-position: right 4px !important;
}

.entry .comment .content {
	padding: 3px 0 !important;
	color: #aaa !important;
}

.entry .comment .quote {
	float:right !important;
}

.entry .comment .content {
	margin-left:0 !important;
	margin-right:21px !important;
}

.friend {
	background-color: #383838 !important;
	-moz-border-radius: 5px !important;
    padding: 2px !important;
}

.owner {
	background-color: #2d2d2d !important;
	-moz-border-radius: 5px !important;
}

#feed .comments .content {
	overflow: hidden !important;
}

.comments .commentform form .text textarea {
	background-color: #fff8ad !important;
	border: 1px solid #000 !important;
	color: #4e4e4e !important;
}

.l_editcomment:before {content: 'ÙˆÙŠØ±Ø§ÙŠØ´ ' !important}

.l_deletecomment:before {content: 'Ø­Ø°Ù ' !important}

.expandcomment {
	direction: ltr !important;
	text-align: center !important;
	font: 10px normal !important;
}

.expandcomment a {
	background-color: #282828 !important;
	padding: 3px !important;
	-moz-border-radius: 5px !important;
}

.expandcomment a:hover {
	background-color: #1e1e1e !important;
}

.ccount, .ccount span {
	color: #ccc !important;
}

.searchbold {
	color: #ff5418 !important;
}

div.box li a[href*=direct].unread {
	color: #ff5418 !important;
}

.profilepopup {
	background-color: #191919 !important;
	border: 2px solid #202020 !important;
}

.profilepopup .picture {
	background-color: #000 !important;
	border: 2px solid #a1310a !important;
}

.profilepopup, .profilepopup table tbody {
	color: #eee !important;
}

.section {
	border-bottom: 1px solid #333 !important;
}

/**** Services Icon back! ****/
a.service {
	font: bold 10px Tahoma !important;
}
a.service[href*=twitter\.com] {color: #00DEEE !important;}
a.service[href*=flickr\.com] {color: #FF0084 !important}
a.service[href*=delicious\.com] {color: #3274D0 !important}
a.service[href*=youtube\.com] {color: #FF1B1B !important}
a.service[href*=google\.com\/reader] {color: #B97373 !important}
a.service[href*=gonahkar\.com] {color: #264788 !important}
a.service[href*=tumblr\.com] {color: #111416 !important}
a.service[href*=diigo\.com] {color: #09f !important}
a.service[href*=furl\.net] {color: #E06300 !important}
a.service[href*=ma\.gnolia\.com] {color: #FEBF10 !important}
a.service[href*=stumbleupon\.com] {color: #02B703 !important}
a.service[href*=goodreads\.com] {color: #5E4C3D !important}
a.service[href*=librarything\.com] {color: #D0895E !important}
a.service[href*=digg\.com] {color: #A8A8A8 !important}
a.service[href*=picasaweb\.google\.com] {color: #803C91 !important}
a.service[href*=zooomr\.com] {color: #E7A715 !important}
a.service[href*=facebook\.com] {color: #3B5998 !important}
a.service[href*=talk\.google\.com] {color: #439F45 !important}
a.service[href*=identi\.ca] {color: #8F0000 !important}
a.service[href*=plurk\.com] {color: #CF682F !important}
a.service[href*=ilike\.com] {color: #F87421 !important}
a.service[href*=last\.fm] {color: #D20039 !important}
a.service[href*=wakoopa\.com] {color: #f03ec8 !important}
a.service[href*=vi\.sualize\.us] {color: #2CACE7 !important}
a.service[href*=polyvore\.com] {color: #000 !important}

#dialog {
	border: 3px solid #000 !important;
	background-color: #222 !important;
	color: #ccc !important;
}

#sidebar .box-body {
	padding-top: 10px !important;
	padding-bottom: 10px !important;
	-moz-border-radius: 0 !important;
	border-left: 1px solid #0f0f0f !important;
	border-right: 1px solid #0f0f0f !important;
	border-bottom: 4px solid #0f0f0f !important;
	border-top: none !important;
	background-color: #202020 !important;
}

}
