css:
@-moz-document domain("youtube.com") {
/* Infut field */
 #container.ytd-searchbox {
    height:40px;
    padding: 0 23px;
    padding-right: 8px;
    /* -1px border */
    border-radius: 20px;
    box-shadow: none !important;
    border: 1px solid var(--ytd-searchbox-legacy-border-color);
    transition: 0.5s cubic-bezier(0.05, 0, 0, 1);
}
#search-input.ytd-searchbox-spt input, #container.ytd-searchbox input.ytd-searchbox {
    line-height: 40px;
    margin: 0;
    padding: 0;
}
/* Button */
 #search-icon-legacy.ytd-searchbox {
    margin-left: 8px;
    border-radius: 20px;
    border: none !important;
}
#search-icon-legacy.ytd-searchbox:hover {
    background: var(--ytd-searchbox-legacy-button-color);
    box-shadow: none;
    transition: 0.5s cubic-bezier(0.05, 0, 0, 1);
}
#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
    color: var(--iron-icon-fill-color);
    opacity: 1 !important;
}
/* Suggestions */
 .sbdd_b {
    padding: 0 8px;
    margin-top: 4px;
    border: none;
    border-radius: 16px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    overflow: hidden;
}
.sbsb_a {
    padding: 8px 0;
}
.sbsb_c {
    border-radius: 16px;
    padding: 0 16px;
    line-height: 32px;
}
}
