@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*-----------------------------------------------------------*/
/*                       Mandarin Popup                      */
/*-----------------------------------------------------------*/

statusbarpanel#mandarinpopup-statusbar 
label#mandarinpopup-statusbar-text {
	-moz-appearance: none;
	font-size: 1px;
	color: transparent;
	padding-bottom: 15px;
	margin-bottom: 0 !important;
	width: 4px;
	background-repeat: no-repeat;
	background-position: center;
	background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8%2F9hAAAAhUlEQVR4Xt2R4QmFIBSFT9EAjtIGz7dRG7TCG8EmsTZwhEZwg9tFbiAXk8I%2F8T74EA940CuICBUsa1Amne1Rx7MjKvRoZJB1xTU%2FNhbyiQ1nwYddWKefIFko5Ca7QWK%2FuEmQvG0G7yzQM5hFjb9TsLFOzCH2q4co%2B5gXWDzD%2FtEvdESEFg5vxx1852T1fwAAAABJRU5ErkJggg%3D%3D");
}
statusbarpanel#mandarinpopup-statusbar 
label#mandarinpopup-statusbar-text[value*="Disabled"] {
	opacity: 0.4;
}
statusbarpanel#mandarinpopup-statusbar 
label#mandarinpopup-statusbar-text[value*="Enabled"] {
	opacity: 1;
}
