css:
@-moz-document url-prefix("https://www.reetro.app/board/") {
.board .column ul li:hover:before,
.color-options .option:hover:before {
    background: transparent;
    top: -20px;
    width: 90%;
    display: inline-block;
    text-align: left;
    position: relative;
}
.board .column ul li:before,
.color-options .option:before {
    width: 90%;
    display: inline-block;
    text-align: center;
}


.board .column ul .colorlblue, .white, 
.color-2 {
    background: lightblue;
    background: hsl(240,100%,90%);
}
.board .column ul .colorlblue:before,
.color-options .color-2:before {
    content: 'B';
}
.board .column ul .colorlblue:hover:before,
.color-options .color-2:hover:before {
    content: 'Lightblue';
}

.board .column ul .colorsblue, .blue, 
.color-5 {
    background: blue;
    background: hsl(240,100%,75%);
}
.board .column ul .colorsblue:before,
.color-options .color-5:before {
    content: 'B';
}
.board .column ul .colorsblue:hover:before,
.color-options .color-5:hover:before {
    content: 'Blue';
}

.board .column ul .colorlgreen, .green, 
.color-4 {
    background: lightgreen;
    background: hsl(120,80%,75%);
}
.board .column ul .colorlgreen:before,
.color-options .color-4:before {
    content: 'G';
}
.board .column ul .colorlgreen:hover:before,
.color-options .color-4:hover:before {
    content: 'Green';
}

.board .column ul .colororange, .yellow, 
.color-1 {
    background: yellow;
    background: hsl(60,100%,55%);
}
.board .column ul .colororange:before,
.color-options .color-1:before {
    content: 'Y';
}
.board .column ul .colororange:hover:before,
.color-options .color-1:hover:before {
    content: 'Yellow';
}

.board .column ul .colorlpink, .pink, 
.color-3 {
    background: pink;
    background: hsl(328,64%,66%);
}
.board .column ul .colorlpink:before,
.color-options .color-3:before {
    content: 'P';
}
.board .column ul .colorlpink:hover:before,
.color-options .color-3:hover:before {
    content: 'Pink';
}

.board .column ul .colorpink, .red, 
.color-7 {
    background: red;
    background: hsl(0,100%,80%);
}
.board .column ul .colorpink:before,
.color-options .color-7:before {
    content: 'R';
}
.board .column ul .colorpink:hover:before,
.color-options .color-7:hover:before {
    content: 'Red';
}

.board .column ul .colorpurple, .purple, 
.color-6 {
	background: mediumpurple;
	background: hsl(260,60%,65%);
}
.board .column ul .colorpurple:before, 
.color-options .color-6:before {
    content: 'P';
}
.board .column ul .colorpurple:hover:before,
.color-options .color-6:hover:before {
    content: 'Purple';
}
}
