css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alexadex.com") {

/* Alexadex Mint Theme (beta 0.1) by CooLynX
------------------------------------*/
/* Alexadex globalie stili
------------------------------------*/
body {
	margin: 0 !important; padding: 0 10px !important;
	font: normal .85em Verdana, sans-serif !important;
	background: #fff;
	color: #000;
}
a, a:visited, a:active {
	color: #000099 !important;
	padding: 0 3px !important;
}
a:hover { 
	color: brown !important;
	background-color: #ffffdd !important;
	border-bottom: 1px dotted brown !important;
}
h2, h3 { 
	color: #008000 !important;
	padding: 4px !important;
	font: bold 1.5em/1em Georgia, serif !important;
}
h3 {
	margin-bottom: 0;
}
input[type="text"], input[type="password"], textarea {
	border-width: 1px !important;
}
input[type="submit"] {
	border-width: 2px;
	background-color: #666;
	color: #BCE27F;
	font-weight: bold;
	font-size: 11px;
	font-family: Verdana, sans-serif;
	height: 2em;
	padding: 2px 4px;
}
input[type="text"]:focus, input[type="password"]:focus, textarea:focus {
	background-color: #ffffee !important;
}
form {
	margin: 5px 10px !important;
	padding: 0 !important;
}

hr {
	border: 0;
	border-bottom: 1px solid #666;
}
table {
	border-collapse: collapse !important;

}


/* Alexadex top
------------------------------------*/
body > table:first-child {
	background-color: #F7F7EE !important;
	border-bottom: 3px solid #666 !important;
}
body > table:first-child td:first-child {
	width: 70% !important;
}
body > table:first-child + hr {
	border: 0;
}
body > table:first-child td {
}

/* Alexadex top menu nav
------------------------------------*/
h1 + p {
	margin: 0 !important;
	padding: 0 !important;
	height: 2.5em !important;
	width: 100% !important;
	font-size: 0 !important;
	text-align: right !important;
}
h1 + p a, h1 + p a:visited, h1 + p a:active {
	background-color: #e7f0d0 !important;
	color: #666 !important;
	border: 1px solid #e7f0d0 !important;
	line-height: 20px !important;
	display: block !important;
	float: left !important;
	margin: 0 1px -2px 0 !important;
	padding: 2px 10px !important;
	font-size: 11px !important;
	font-weight: bold;
	font-family: Verdana, Arial, sans-serif !important;
	-moz-border-radius: 8px 8px 0 0;
}
h1 + p a:hover {
	border-bottom: 1px solid #666 !important;
	background-color: #666 !important;
	color: #bce27f !important;
}
/* Alexadex extra bloks
------------------------------------*/
h2 + p, p + p {
	border: 1px dashed #ddd !important;
	background-color: #fbfbfb !important;
	padding: 5px !important;
}
h2 + p + table, h2 + table {
	background-color: #F7F7EE !important;
	border: 1px solid #e7f0d0;
	width: 100%;
}
h2 + p + table td, h2 + table td {
	padding: 2px 4px;
}
h2 + p + table td b, h2 + table td b {
	color: #666;
	font-weight: normal !important;
}
h2 + p + table td b > a:after, h2 + table td b > a:after {
	color: red;
	font-weight: bold;
	content: "*";
}
h2 + p + table td[align="right"],
h2 + table td[align="right"] {
	font-weight: bold !important;
}
p + p {
	margin: 5px 10px;
}
	
/* Gar√¢ akciju tabula 
------------------------------------*/
h3 + table[cellpadding="2"], p + table[cellpadding="2"],
ul + table[cellpadding="2"] {
	border-left: 1px solid #e7f0d0 !important;
}

/* Tabulas headeris */
table[cellpadding="2"][width="100%"] tr:first-child td
 {
	padding: 2px !important;
	border-bottom: 1px solid #fff !important;
}
table[cellpadding="2"] tr:first-child td,
table[cellpadding="2"] tr:first-child:hover td,
table[cellpadding="2"] tr:first-child td a:hover,
table[cellpadding="2"] tr:first-child td:first-child {
	background-color: #666 !important;
	text-align: center !important;
}
table[cellpadding="2"] tr:first-child td a {
	color: #bce27f !important;
	font-size: .85em !important;
}
table[cellpadding="2"] tr:first-child td a[href$="desc"]:after {
	content: " \2191";
	font-size: 1.5em !important;
}
table[cellpadding="2"] tr:first-child td a[href$="asc"]:after {
	content: " \2193";
	font-size: 1.5em !important;
}	
h4:first-letter,
h1 + p a:first-letter,
h2:first-letter, h3:first-letter,
h2 + p + table tr td:first-child:first-letter,
h2 + p + table tr td:first-child b:first-letter,
h2 + table tr td:first-child:first-letter,
h2 + table tr td:first-child b:first-letter,
table[cellpadding="2"] tr:first-child td:first-letter,
table[cellpadding="2"] tr:first-child td a:first-letter {
	text-transform: capitalize !important;
}
table[cellpadding="2"] tr:first-child td a:hover {
	border-bottom: none !important;
}
	
/* Tabulas rindas */
table[cellpadding="2"] tr td {
	padding: 2px 4px !important;
	color: #666 !important;
	border-right: 1px solid #e7f0d0 !important;
	border-bottom: 1px solid #e7f0d0 !important;
}
table[cellpadding="2"] tr td {
	empty-cells: show !important;
}
table[cellpadding="2"] td[bgcolor="#dddddd"] {
	background-color: #f7f7ee !important;
}
h3 + table td font[color="red"],
p + table td font[color="red"],
ul + table td font[color="red"] {
	color: #fa4d4d !important;
}
table[cellpadding="2"] tr:hover td {
	background-color: #ffffdd !important;
	color: #000 !important;
}
h3 + table tr:hover td font[color="red"],
p + table tr:hover td font[color="red"],
ul + table tr:hover td font[color="red"] {
	color: #ff0000 !important;
}
table[cellpadding="2"] tr td:first-child {
	text-align: right !important;
}
table[cellpadding="2"] tr td:last-child,
table[cellpadding="2"] tr td:first-child + td + td + td + td + td font[color="black"] {
	color: #339933 !important;
}
/* h3 + table[cellpadding="2"] tr td:last-child bug? */
table[cellpadding="2"] tr td:first-child + td + td + td + td + td + td + td[align="right"]
 {
	font-weight: bolder !important;
}
table[cellpadding="2"] tr:hover td:last-child,
table[cellpadding="2"] tr:hover td:first-child + td + td + td + td + td font[color="black"] {
	color: #008000 !important;
}

/* Atcelt poga */
i small a, small i a {
	background-color: white !important;
	padding: 0 0 1px 2px !important;
	margin: 0 !important;
	border: 1px solid #e7f0d0;
	font-style: normal !important;
	color: #AA6565 !important;
	font-size: .9em !important;
}
i small a:hover, small i a:hover {
	background-color: white !important;
	color: red !important;
	border-bottom: 1px solid #e7f0d0 !important;
}
i small a:after, small i a:after {
	content: "x";
	color: #fff;
	background-color: #E7F0D0;
	padding: 0 2px 1px 4px !important;
	line-height: 10px;
	font-size: 10px;
	font-weight: bolder;
	margin: 0 0 0 5px;
}
i small a:hover:after, small i a:hover:after {
	color: red !important;
}


/* Apaksheejaa UL vizualizaacija 
------------------------------------*/
h3 + ul, h3 + table ul {
	margin: 0 auto 10px 0;
	padding: 0;
	list-style: none;
	border: 1px solid #e7f0d0;
	border-bottom: none;
}
h3 + ul li, h3 + table ul li {
	color: #000;
	display: block;
	border-bottom: 1px solid #e7f0d0 !important;
	margin: 0;
	padding: 2px 10px 2px 2px;
}
h3 + ul li:hover, h3 + table ul li:hover {
	background-color: #F7F7EE;
}
h3 + ul li:before, h3 + table ul li:before {
	content: "\00BB";
	color: #fff;
	background-color: #E7F0D0;
	height: 10px;
	padding: 1px 2px;
	line-height: 10px;
	font-size: 12px;
	font-weight: bolder;
	margin-right: 5px;
}

/* Forums 
------------------------------------*/
hr + table[width="100%"] {

}
hr + table td[width="38%"] {

}
hr + table td[width="58%"] {

}
textarea {
 	width: 95% !important;
	padding: 5px;
}
textarea:focus {
 	height: 15em !important;
}
.comment {
	background-color: #fff !important;
	border: 1px solid #e7f0d0 !important;
	margin-top: 5px !important;
	margin-bottom: 5px !important;
}
.comment-post {
	padding: 5px !important;
}
.comment-post strong {
	color: #666;
	background-color: #E7F0D0;
	padding: 1px 3px !important;
	line-height: 10px;
	font-size: 10px;
	font-weight: bolder;
	margin: 0;
}
.comment-meta {
	margin: 0 !important;
	padding: 4px !important;
	background-color: #f7f7ee !important;
	color: #666 !important;
}
.comment[style*="1em"] {margin-left: .5em !important; }
.comment[style*="2em"] {margin-left: 1em !important; }
.comment[style*="3em"] {margin-left: 1.5em !important; }
.comment[style*="4em"] {margin-left: 2em !important; }
.comment[style*="5em"] {margin-left: 2.5em !important; }
.comment[style*="6em"] {margin-left: 3em !important; }
.comment[style*="7em"] {margin-left: 3.5em !important; }
.comment[style*="8em"] {margin-left: 4em !important; }
.comment[style*="9em"] {margin-left: 4.5em !important; }
.comment[style*="10em"] {margin-left: 5em !important; }


}
