css:
@-moz-document url-prefix("https://kun.uz") {
body,.countries {
    color: #eee!important;
    background: #191A1D!important;
}
body a {
    color: #eee!important;
    /*color: #ffdd40!important;*/
}
/*AD disable*/
.page-header__adv{
display:none!important;
}

/*link*/
a{
 color:#ffdd40!important;
}

/*news*/
.news-meta > span{
 color:#ffdd40!important;
}

.small-news .small-news__title{
color:#f5f5f5!important;
}

.news-lenta__title{
color:#f5f5f5!important;
}


.news-lenta__title:hover{
color:#09D4FF!important;
}

.news-lenta{
background: #2c303a!important;
color:#f5f5f5!important;
border-bottom:none;
padding:6px!important;
}

.small-news:hover .small-news__title{
color:#09D4FF!important;
}

.news .news__title:hover{
color:#09D4FF!important;
}

.small-news__content{
border-bottom: 1px solid #2c303a!important;
}
.big-news__content{
background: #2c303a!important;
color:#fff!important;

}

.big-news__title{
color:#f5f5f5!important;
}


.big-news__title:hover{
color:#09D4FF!important;
}

.big-news__description{
color:#fff!important;
}

.big-news__title:hover{
color:#09D4FF!important;
}

.news-business{
background:#191A1D!important;
color:#f5f5f5;
}

.news-business__title{
color:#f5f5f5!important;
}

.news-business__title:hover{
color:#09D4FF!important;
}

.bg-gray{
background: #191A1D!important;
}
.block-title span{
color: #fff;
}

.single-content blockquote:not([class]){
background: #2c303a!important;
color: #fff!important;
}
.single-content blockquote:not([class]) p,.single-content{
color: #ccc!important;
}

p,h1,h2,h3,h4,h5,h6{
color:#f5f5f5!important;
}

.all-link{
color:#09D4FF!important;
}


.all-link:hover{
color:#F5F5F5!important;
}
/**/
.post-line{
border:none;
}

.post-line .line-block{
border-left: 1px solid #09D4FF;
}

.post-line .line-block .start-date{
color: #ffdd40 !important;
}

.line-post .lp-head .publish-date::after {
background-color:#09D4FF!important;
border:#09D4FF;
}

.line-block .line-post:last-child .lp-body{
background:#2c303a !important;
padding:10px!important;
}

.single-content ul li::before {
background-color:#09D4FF!important;
}

.single-header__title{
color:#ddd!important;
}

ul.list > li a{
color:#09D4FF!important;
}

ul.list > li a:hover{
color:#FFF!important;
}

/*Right*/
.news-recommended{
color: #f5f5f5!important;
background: #2c303a!important;
}

.news-recommended__title{
color: #f5f5f5!important;
}

.news-recommended__title:hover{
color:#09D4FF !important;
}

/*slider*/
.business-block__slider{
background:#2c303a !important;
}

.load-more__link{
background:#09D4FF !important;
border-radius:5px;
color:#333;
}

.top-news__big .big-news__title:hover {
color:#09D4FF !important;
}

.single-header__meta{
color:#ffdd40 !important;
}


.share-news .share-news-dropdown{
background:#2c303a !important;
}

.interview{
background: #2c303a!important;
}
}
