@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*
 * Tab Progress Line for Strata40 0.4.3 by SpewBoy http://spewboy.deviantart.com/art/Strata40-v0-4-3-132191373
 * 
 * Version: 1.0
 * Author: Christian "KriLL3" Gerefalk
 * Work in progress.
 * Dependancies: Tab Mix Plus https://addons.mozilla.org/en-US/firefox/addon/1122
 */

.tabbrowser-tabs .tab-progress
{
margin: -4px 0px 0 -23px !important;
opacity: 1 !important;
-moz-border-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAEwAAAADCAYAAAA5memSAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACdJREFUeNpitLe362QYBUQDloM6h6RGg4GEAHO67vRsNBiIBwABBgCCaQVZoG925gAAAABJRU5ErkJggg==") 27 0 0 0 / 3px stretch !important;
}



.tabbrowser-tabs .tab-progress .progress-bar
{
margin: -3px -3px 0 -2px !important;
-moz-border-image: url("data:image/png;base64,") 7 0 0 0 / 3px stretch !important;
background: transparent !important;
}

.tabbrowser-tab[selected="true"] .tab-progress
{
margin: -6px 0px 0 -26px !important;
}

.tabbrowser-tab:hover .tab-progress
{
margin: -6px 0px 0 -26px !important;
}

.tabbrowser-tabs .tabbrowser-tab[selected="true"] .tab-progress, .tabbrowser-tabs .tabbrowser-tab[showbutton="on"] .tab-progress
{
display: none !important;
}



