css:
@-moz-document domain('skyroom.online')
{
  :root
  {
    --color1: #232329;
    --color2: #232329;
    --color3: #2D6469;
    --color4: #9794a1;
    --color5: #9794a1;
    --color6: #9794a1;
    --color7: #3465A4;
    --color8: #33739055;
    --color9: #8e91a3;
    --color10: #9794a1;
    --color11: #2D657799;
    --color12: #2F696D;
    --color13: #000000;
    --text-color: #ffffff;
  }

  *
  {
    outline: none;
  }

  .icon {
    display: inline-block;
    width: 24px;
    height: 24px;
    vertical-align: middle;
    fill: var(--color2);
}

  .btn-flat
  {
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: transaprent;
    border-radius: 20px;
    transition: background-color ease-in-out  200ms;
    border: none;
  }

  .btn-flat:hover
  {
    background-color: var(--color2) !important;
    border: none !important;
  }

  #toolbar button.btn-on .icon
  {
    border: none;
  }

  #toolbar .btn-flat
  {
	background-color:transparent ;
    /*background-color: var(--color2);*/
  }

  #app_menu button
  {
    background-color: var(--color5);
    width: 50px !important;
    height: 50px !important;
    box-shadow: 0 0 3px .25px var(--color5) !important;
    outline: none;
  }

  #app_menu button:hover
  {
    border: none !important;
    animation: spin .5s linear .5;
  }

  .btn-on
  {
	/*background-color:var(--color2) !important;*/
    background-color: var(--color4) !important;
  }

  .user-nickname
  {
    min-width: 30ch;
  }

  header
  {
    display: none !important;
  }

  a
  {
    color: black;
   
  }

  #workspace
  {
	/*background-color:var(--color2);*/
    background-image: url('https://i.ibb.co/n31sy9W/yuriy-kovalev-n-N1-HSDt-Kdlw-unsplash.jpg');
    border: none;
  }

  #room_timer
  {
    color: transparent;
  }

  .btn_flat
  {
    background-color: transparent;
  }

  .block
  {
    background-color: var(--color4);
    border-radius: 20px;
    border: none;
/* 	box-shadow: 0 0 2px .25px black !important; */
    padding: 10px;
    transition: box-shadow ease 300ms;
  }

  .block:hover
  {
    box-shadow: 10px 5px 20px .5px black !important;
  }

  .text, .username
  {
    color: white;
  }

  .message-box-content, .pinned-message
  {
    background-color: var(--color5) !important;
  }

  .message-box-wrapper
  {
    border: none;
  }

  .users-list-wrapper
  {
    border: none;
    background-color: transparent;
  }

  .ps__rail-y .ps__thumb-y
  {
    display: none;
    background-color: red;
  }

  .entry
  {
    background-color: var(--color6);
    border: none;
  }

  #page_room
  {
	/*background-color:var(--color2);*/
    background-color: transparent;
  }

  #toolbar
  {
    /*box-shadow: 0 0 3px .25px black !important;*/
	/*background-color:var(--color6);*/
    background-color: transparent;
    padding: 10px;
    border-radius: 10px;
  }

  .trademark
  {
	/*display:auto;*/
/* 	background-color:var(color2); */
    background-color: transparent !important;
  }

  body
  {
    margin: 0;
    background-image: url('https://i.ibb.co/n31sy9W/yuriy-kovalev-n-N1-HSDt-Kdlw-unsplash.jpg');
    font-family: var(--font-family);
    font-size: var(--font-size);
    color: white;
  }

  .trademark::after
  {
    content: 'Theme By Kiyarash Farahani';
    font-size: .8rem;
	/*background-color:#363636;*/
    border-radius: 20px;
    color: white;
/* 	font-size:10px; */
    margin-left: 10px;
    padding: 2px;
    opacity: .1 !important;
    transition: opacity ease 500ms;
  }

  .trademark:hover::after
  {
    opacity: 1 !important;
  }

  .user-row
  {
    transition: background-color ease 300ms;
  }

  .user-row:hover
  {
    background-color: #fff0 !important;
    background-image: linear-gradient(to right, #fff0,#fff5,#fff0) !important;
  }

  .ps__thumb-x
  {
    display: none !important;
  }

  .trademark a
  {
    display: none;
    background-color: white;
    padding: 2px;
    border-radius: 20px;
  }

  .input
  {
    background-color: var(--color5);
  }

  .chat .chat-msg
  {
    display: flex;
    flex-direction: column;
    border-radius: 10px;
    background-color: #444f57;
  }

  .box-grow .row
  {
    background-color: transparent;
  }

  .box-grow .icon-button
  {
    background-color: transparent;
    width: 25px;
    height: 25px;
    justify-content: center;
    align-items: center;
    padding: 1px;
  }

  .commands-box .row
  {
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 20px;
    overflow: hidden;
    background-color: var(--color4);
  }

  .commands-box
  {
/* 	border-radius:20px;	 */;
  }

  .col .commands-box
  {
    background-color: var(--color4);
  }

  .chat .chat-msg .message-commands
  {
    background-color: transparent;
  }

  .chat .chat-msg .quoted-message
  {
    background-color: transparent;
    border-radius: 5px;
    border: none;
  }

  .entry, .input, input, select, textarea
  {
    border: none;
  }

  .input.box.dont-break-out.is-rtl
  {
    border-radius: 20px;
    border: none;
    color: white;
  }

  .input .box .dont-break-out .is-rtl::placeholder
  {
    color: white;
  }

  .row .block-header
  {
    background-color: transparent;
  }

  .row .toolbar .toolbar2
  {
    background-color: var(--color4);
  }

  .box-grow .row
  {
    background-color: transparent;
  }

  .chat .message-box-content .ps__thumb-y
  {
    display: none;
  }

  .user-action .btn-flat
  {
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: var(--color4);
    width: 25px;
    height: 25px;
    margin-right: 50px;
  }

  .login-page
  {
    background-image: url('https://i.ibb.co/n31sy9W/yuriy-kovalev-n-N1-HSDt-Kdlw-unsplash.jpg');
  }

  .menu-items
  {
    background-color: var(--color2);
    border-radius: 10px;
    box-shadow: 5px 2px 30px .25px black;
  }

  .menu-items li
  {
    transition: color ease 200ms;
    transition: background-color ease 200ms;
  }

  .menu-items li:hover
  {
	/*background-color:#D0FCFF;*/
    color: black;
  }

  .user-icon
  {
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: transparent;
    border-radius: 20px;
    width: 20px !important;
    height: 20px !important;
  }

  .user-nickname
  {
    color: white;
    margin-right: 10px;
  }

  .video-wrapper .row
  {
    background-color: transparent;
  }

  .video-wrapper .media-title, .slide-number
  {
    background-color: var(--color11);
    border-radius: 20px;
    border: none !important;
  }

  .start-icon *
  {
    border: none !important;
  }

  .media-player.video .controls
  {
    background-color: transparent !important;
  }

  .controls-wrapper .controls .start-icon, .slideshow .start-icon
  {
    border-radius: 20px;
    padding: 5px;
    width: 30px;
    height: 30px;
    background-color: var(--color10);
  }

  .slideshow .icon-button
  {
    background-color: transparent;
    border: none;
  }

  .slideshow .icon-button:hover
  {
    background-color: transparent;
    border: none;
  }

  #login_block .col-start .box-grow
  {
    background-color: transparent;
  }

  .login-form input
  {
    color: black;
    background-color: #5CE7FF00 !important;
    border-radius: 20px;
    text-align: center;
  }

  .login-form input:hover
  {
  }

  #btn_login, #btn_guest
  {
    background-color: var(--color9);
    border-radius: 50px;
  }

  #btn_guest
  {
    background-color: var(--color8);
  }

  .login-form .row
  {
    background-color: transparent;
  }

  .block-title
  {
    background-color: transparent !important;
    border: none !important;
  }

  #login_desc
  {
    display: none;
  }

  .login-title
  {
    display: none;
  }

  .slideshow .row
  {
    background-color: transparent;
  }
}
