css:
@-moz-document domain("sel3.ebsoc.co.kr"), domain("sel2.ebsoc.co.kr"), domain("sel1.ebsoc.co.kr"), domain("chj.ebsoc.co.kr")
{
    body {
        color: #888888;
        font-size: 14px;
        font-family: 'Roboto','Noto Sans KR',Malgun Gothic,'맑은고딕',Dotum,'굴림',Gulim,Helvetica,sans-serif;
        letter-spacing: -0.5px;
        background-color: #2a2a2a;
         
          
  }
    .index_class .sec_tit_area .date {
    font-size: 18px;
    color: #fff;
    margin-right: 5px;
    vertical-align: middle;
    display: inline;
  }
    
   a {
    color: #fff;
    text-decoration: none;
}

.class_list > li .cont {
    padding: 30px;
    background-color: #000000;
}
.class_list > li .cont .info > dl > dt a {
    display: block;
    overflow: hidden;
    position: relative;
    color: #fff;
    text-overflow: ellipsis;
}
.schedule_card {
    position: relative;
    padding: 18px 15px 13px 15px;
    border: 1px solid #000000;
    border-left: 4px solid #ccc;
    border-radius: 5px;
    color: #666;
    background: #000;
    box-shadow: 1.9px 2.3px 10px 0 rgba(0, 0, 0, 0.1);
    -webkit-box-shadow: 1.9px 2.3px 10px 0 rgba(0, 0, 0, 0.1);
    height: 128px;
}
::-webkit-scrollbar-track {
    background: #2a2a2a;
}
#mainFooter .footer {
    position: relative;
    padding: 30px 0 50px;
    color: #999;
    background-color: #000;
}
.card_list > li.nodata p {
    display: table-cell;
    color: #FFFFFF;
    font-size: 16px;
    line-height: 22px;
    vertical-align: middle;
}
.card_list > li.nodata {
    min-height: 274px;
    padding-bottom: 0;
    text-align: center;
    background: #000;
}
  .list_box.nodata > p {
    display: table-cell;
    color: #aaa;
    font-size: 15px;
    font-weight: 500;
    vertical-align: middle;
    text-align: center;
    background-color: #4c4c4c
}
  .list_box {
    overflow: hidden;
    height: 260px;
    padding: 40px;
    border-radius: 5px;
    background-color: #4c4c4c;
}
  .list_box .list > li > a span {
    display: inline-block;
    color: #fff;
    font-size: 15px;
    line-height: 20px;
    vertical-align: top;
}
.card_list.student > li {
    padding-bottom: 0;
    background: #000000 url(https://static.ebsoc.co.kr/frontend/img/bg_card_list_wh.bccb6ce.png) no-repeat 100% 0;
}
.card_list > li .bottom {
    margin-top: 12px;
    position: absolute;
    padding: 20px 27px 30px;
    box-sizing: border-box;
    background-color: #4a4a4a;
    bottom: 0;
    left: 0;
    right: 0;
}
.card_list > li .bottom .summ_info p, .card_list > li .bottom .summ_info div {
    margin-bottom: 5px;
    margin-right: 20px;
    color: #fff;
    font-size: 14px;
    line-height: 20px;
    vertical-align: top;
}
.table3Days .today {
    background: #2a2a2a;
}
.page_intro .sec_landing .area .box .tit {
    display: block;
    position: relative;
    font-size: 30px;
    font-weight: 400;
    line-height: 36px;
    word-break: keep-all;
    color: #000000;
}
#header .user_summary > li .pop .cont .eps2 {
    margin: 4px 0 1px;
    font-size: 15px;
    line-height: 20px;
    color: black;
}
}
