css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(facebook.com) {

.location div.location_composer .location_close a{color:#c4c4c4!important}
.diagnostics{border:1px solid #444444!important;background:#efefef!important}
#chat_tab_bar .tab_handle.highlight.disabled{color:#d1d1d1!important}
.fb_menu_dropdown .fb_menu_item .fb_menu_item_link:hover{background-color:#909090!important;border-top-color:#878787!important}
.contextual_dialog .contextual_dialog_content{border:1px solid #b7b7b7!important}
.fb_menu_dropdown .fb_menu_item .fb_menu_item_link:hover,.contextual_dialog .contextual_dialog_content{border-bottom-color:#696969!important}
.presence_menu_opts .list_select li div em{background-color:#e2e2e2!important}
.minifeedwall .empty_wall .status{background:#f5f5f5!important;border-top:1px solid #eaeaea!important}
.profile .add_new_box_border .border_frame{border:solid 10px #868686!important}
.UIButton_Blue{background-color:#828282!important;border-color:#4a4a4a #3a3a3a #303030!important}
#buddy_list_panel .flyout_open a.panel_item span{border-top:1px solid #4a4a4a!important}
#buddy_list.reorder_fl ul li.friend_list div.friendlist_status{background:#ededed!important;border-top:1px solid #d0d0d0!important}
#buddy_list.reorder_fl ul li.friend_list div.friendlist_status,.profile .top_bar ul.tabs li.profile_tab_more .tabs_more_menu ul li a.tab_link{color:#000!important}
div.location_flyout .menu_content .inputtext{border:1px solid #656565!important}
.location div.location_composer{background-color:#f3f3f3!important;border-color:#cbcbcb!important;color:#3d3d3d!important}
.flash_fallback{background:#f8f8f8!important;border:1px solid #e1e1e1!important}
.dark_dialog_overlay{background-color:#747474!important}
.profile .top_bar ul.tabs li.profile_tab_more .tabs_more_menu ul li a.tab_link{border-bottom:1px solid #ededed!important}
.contextual_dialog .contextual_dialog_content,.profile .top_bar ul.tabs li.profile_tab_more .tabs_more_menu ul li a.tab_link{background-color:#f7f7f7!important}
.UIButton_Green .UIButton_Text{text-shadow:0 1px 0 #5f5f5f!important}
.UIGuide_LockDisclaimer{color:#797979!important}
.generic_dialog div.dialog_loading{background-color:#f2f2f2!important;border:1px solid #606060!important}
.share_media .fb_videomsg{background:#0d0d0d!important}
#presence_bar div.application_menu_divider{border-bottom:1px solid #e9e9e9!important;color:#808080!important}
#presence_bar .presence_menu_opts h2 .presence_minimize{background-color:#c2c2c2!important}
.nux_highlight_composer{color:#262626!important}
.UIButton_Green{background-color:#7a7a7a!important;border-color:#494949 #393939 #2f2f2f!important}
.UIBeeper .UIBeep{background:#e8e8e8!important;border-bottom:1px solid #cecece!important;border-left:1px solid #a0a0a0!important;border-right:1px solid #a0a0a0!important}
.UIButton_Depressed{background-color:#7d7d7d!important;border-color:#525252!important}
.UIButton_Gray{background-color:#eaeaea!important;border-color:#888!important}
.UIButton_Blue:active{background-color:#757575!important}
div.standard_tokenizer div.tokenizer{border:solid 1px #a6a6a6!important}
.UIBeeper .UIBeep_Selected{background:#d5d5d5!important}
.friend_guesses.list{background:#f9f9f9!important}
.remove_option{border:solid 1px #dbdbdb!important;border-bottom:solid 1px #ccc!important;color:#696969!important}
.UIMutableFilterList_Separator{border-top:1px solid #b2b2b2!important}
#sidebar_ads .create_button{border:1px solid #484848!important}
.editpicture .editor_panel{border:1px solid #d7d7d7!important;border-top:1px solid #898989!important}
.create_button_link{border-top:1px solid #a0a0a0!important}
.UIComposer_PrivacyCallout_Title,.fb_menu_dropdown .fb_menu_item .fb_menu_item_link:hover,#buddy_list_panel .flyout_open a.panel_item span,.UIButton_Green .UIButton_Text,.create_button_link{color:#fff!important}
.create_button_link,.UIComposer_PrivacyCallout_Title{background-color:#787878!important}
.UIContentBox_White{border-color:#d6d6d6!important}
.UIContentBox_White,.remove_option{background-color:#fff!important}
.UIContentBox_GrayBlueTop{background-color:#f0f0f0!important;border-color:#c8c8c8!important;border-bottom-color:#c0c0c0!important;border-top:2px solid #808080!important}
.presence_menu_opts .presence_menu_subheader h3{border-bottom:1px solid #cacaca!important;color:#222!important}
.UIComposer_PrivacyCallout_Text{border:1px solid #545454!important}
.UICantSeeProfileBlurbText{color:#686868!important}
.share_and_hide .share_a{border:solid 1px #9d9d9d!important}
.flash_fallback_header{color:#2c2c2c!important}

}
