@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#ad-blocking-makes-fella-sad, .ads { display: none !important; }

.stream-ads { border-right:0px none !important; }

#deviantART-v5 div.thought,
#deviantART-v5 div#artist-comments		{margin-left:0 !important; margin-right:0 !important; width:auto !important; max-width:100% !important;}

#deviantART-v5 div#artist-comments div.details	{font-size:80% !important; max-width:200px !important;}

/* Deviation links */

#deviantART-v5 div.tool-links			{margin-left:-15px !important;}

/* Mood box */

#deviantART-v5 table.f.moods del		{display:none !important;}
#deviantART-v5 table.f.moods strong		{font-size:100% !important;}
#deviantART-v5 table.f.moods img		{display:none !important;}
#deviantART-v5 table.f a.moodx			{top:12px !important;}

/* Comments */

#deviantART-v5 div.thought			{padding:5px 5px 8px 0 !important; min-height:80px !important;}
#deviantART-v5 div.thought.collapsed-thought	{min-height:0 !important; padding:0 !important;}
#deviantART-v5 div.thought div.body		{margin-top:10px !important; font-size:90% !important;}
#deviantART-v5 div.thought div.body.preview	{margin:0 !important;}
#deviantART-v5 div.thought a.h			{text-decoration:none !important;}
#deviantART-v5 div.thought img.mood		{top:5px !important; right:5px !important;}

/* Hides the llama mood! Very useful! */

#deviantART-v5 div.thought img.mood[alt="Mood: Llama"]
						{display:none !important;}

/* Hover nested comments in two colour schemes */

#deviantART-v5 div.nest				{padding-left:10px !important; margin:0 0 8px !important; border-left:solid 3px #BAC5BA !important;}
#deviantART-v5 div.nest:hover			{border-color:#92A399 !important;}

#deviantART-v5 #deviation div.nest,
#deviantART-v5 #news div.nest,
#deviantART-v5 #comment div.nest		{border-color:#EAF2EE !important;}

#deviantART-v5 #deviation div.nest:hover,
#deviantART-v5 #news div.nest:hover,
#deviantART-v5 #comment div.nest:hover		{border-color:#D3E0DC !important;}


}
