div#tads, /* Ad */
table#mbEnd, /* Ad */
table#sft h1,
table#sft td.xsm,
span.xsm{
    display:none !important;
}
/* グローバルナビゲーションの枠線 */
 
body > div > div#gbar{
    /*padding-bottom: 15px !important;*/
}
 
body > div > div.gbh{
    border: none !important;
    /*padding-bottom: 10px !important; */
