css:
@-moz-document url-prefix("http://protic-portail.csdecou.qc.ca/protic5/") {
:root {
    background:#191a1b
}
html,

#main-content a.rss {
    color: #acacac;
}
.home.blog, #main-content, body, .shadow{
    background:#191a1b
}

.holder-footer, .shadow, .second-menu{
    background-color: #191a1b
}

.see-all, table#wp-calendar thead {
    background: #009c94;
}

#realisation, #facebook, .thumbnail, .soft-top, .page .soft-bottom {
display:none;
}


.bandeau {
  content: url("https://i.ibb.co/vvvs8Rk/PROTIC5-BANDEAU-001.jpg");
    margin-left: 50px;
}

.holder-960 {
  background: #191a1b;
  width: 960px;
  margin: 0 auto; }

div#header {
    position: relative;
    z-index: 18;
    min-height: 175px;
    padding: 0 25px;
}

#content {
    background: #191a1b;
    position: relative;
    z-index: 10;
    padding-top: 35px;
    top: -1px;
    margin-top: 10px;
}

#wrapper {
    height: auto !important;
    height: 100%;
    min-height: 100%;
    -webkit-box-shadow: 0px 0px 0px #191a1b;
    -moz-box-shadow: 0px 0px 0px #191a1b;
    box-shadow: 0px 0px 0px #191a1b;
}

div#header #main-nav > ul > li, .menu > ul > li {
    background: #acacac;
    border-top: 0px solid #ecdbb8;
    border-bottom: 0px solid #ecdbb8;
}

div#header #main-nav > ul, div#header .menu > ul {
    -webkit-border-top-left-radius: 0px;
    -moz-border-radius-topleft: 0px;
    border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    -moz-border-radius-topright: 0px;
    border-top-right-radius: 0px;
    -webkit-border-bottom-right-radius: 0px;
    -moz-border-radius-bottomright: 0px;
    border-bottom-right-radius: 0px;
    -webkit-border-bottom-left-radius: 0px;
    -moz-border-radius-bottomleft: 0px;
    border-bottom-left-radius: 0px;
    -moz-box-shadow: 0px 0px 0px #dbdcdc;
    -webkit-box-shadow: 0px 0px 0px #dbdcdc;
    box-shadow: 0px 0px 0px #dbdcdc;
    display: inline-block;
}


div#header #meta-nav ul li a {
    color: #009c94;
    font-size: 13px;
    font-size: 1.3rem;
    text-decoration: none;
    -webkit-transition: 0.1s linear;
    -moz-transition: 0.1s linear;
    -o-transition: 0.1s linear;
    -ms-transition: 0.1s linear;
    transition: 0.1s linear;
}

.see-all {
    color: #acacac !important;
    font-size: 16px;
    font-size: 1.6rem;
    text-align: right;
    padding: 5px 20px 5px 10px;
    -moz-box-shadow: 0px 0px 0px #8d8d8d;
    -webkit-box-shadow: 0px 0px 0px #8d8d8d;
    box-shadow: 0px 0px 0px #8d8d8d;
}

#events #events-list {
    background: #191a1b;
    border: 0px solid #acacac;
}

hr {
  border-top: 0px solid #bbbbbb;
  border: 0;
  display: block;
  height: 1px;
  margin: 1em 0;
  padding: 0; }


table#wp-calendar tbody td {
    color: #acacac;
}
#eo-widget-next-month a {
    background: #009c94 url('http://protic-portail.csdecou.qc.ca/protic3/wp-content/themes/csdecouvreurs-secondaire/assets/images/layout/ico-arrow-right.png') no-repeat center;
}
#eo-widget-prev-month a {
    background: #009c94 url('http://protic-portail.csdecou.qc.ca/protic3/wp-content/themes/csdecouvreurs-secondaire/assets/images/layout/ico-arrow-left.png') no-repeat center;
}
table#wp-calendar tbody td {
    background: #212224;
    padding: 7px 0;
    text-align: center;
    border: 0px solid #009c94;
}
#main-content #news article {
    border-bottom: 1px dotted #009c94;
}
#news article.headline .headline-holder {
    background: white;
    border-bottom: 0px #ecdbb8 solid;
}

img[src="http://protic-portail.csdecou.qc.ca/protic5/wp-content/themes/csdecouvreurs/assets/images/layout/ico-magnify.png"] {
-webkit-filter: grayscale(100%); 
  filter: grayscale(100%);}
table#wp-calendar-header .pad {
    color: #009c94;
    text-shadow: 0px 0px 0px #009c94;
}




.page #sidebar {
    background: #191a1b !important;
    border-right: 0px solid #ecdbb8;
}





div#header #main-nav > ul li:first-child {
    border-left: 0px solid #ecdbb8;
}


#eo-widget-prev-month a, #eo-widget-next-month a {
    
    -moz-box-shadow: 0px 0px 0px #8d8d8d;
    -webkit-box-shadow: 0px 0px 0px #8d8d8d;
    box-shadow: 0px 0px 0px #8d8d8d;
}

#main-content a {
    color: #009c94;
}


#main-content h1 {
    margin: 0px 0;
}

footer#footer #footer-meta-nav ul li a {
    color: #009c94;
}

.the-content {
    color: #acacac;
}

ul.sub-pages li a {
    background: #191a1b url('http://protic-portail.csdecou.qc.ca/protic3/wp-content/themes/csdecouvreurs-secondaire/assets/images/layout/ico-arrow-menu.png') no-repeat 10px 13px;
    border-bottom: 1px solid #212224;
    border-left: 1px solid #212224;
    border-right: 1px solid #212224;
    color: #acacac;
}
.page #sidebar {
    background: #191a1b !important;
    border-right: 0px solid #ecdbb8;
}
.page #sidebar .second-menu ul li.active > a {
    background: #212224;
    color: white
}
.sidebar-sub {
    display: block;
    border-left: 0px solid #fbe9c3;
    border-bottom: 0px solid #fbe9c3;
    background: #191a1b;
    -webkit-border-bottom-left-radius: 10px;
    -moz-border-radius-bottomleft: 10px;
    border-bottom-left-radius: 10px;
}

::selection {
    color: #333;
    background-color: #acacac
}

.main-section {
    color: #acacac
}
.page #sidebar .second-menu ul ul.sub-pages li a:hover, .page #sidebar .second-menu ul ul.sub-pages li.active-sub a {
    background: rgba(0,156,148,0.50) !important;
}

.sub-nav .orange {
    background: #009c94;
    border-left: 1px solid #009c94;
    border-right: 1px solid #009c94;
}
.sub-nav {
    border-bottom: 0px solid #d3d3d3;
    border-top: 1px solid #009c94;
}
.page #sidebar .second-menu ul ul.sub-pages li a {
    border: none;
    background:#191a1b !important;
    font-size: 15px;
    padding: 5px 45px;
    display: block;
    margin: 0;
}
div#header #main-nav > ul > li:hover > a {
    text-shadow: 1px 1px 3px #009c94;
}

div#header #main-nav > ul > li:hover, .menu > ul > li:hover { 
	background: #009c94 url('http://protic-portail.csdecou.qc.ca/protic3/wp-content/themes/csdecouvreurs-secondaire/assets/images/layout/bg-primary-nav-shadow.png') no-repeat top left; 
	border-top: 0px solid #3180ab; 
	border-bottom: 0px solid #3180ab; }
div#header #main-nav > ul > li:hover > a { text-shadow: 1px 1px 3px #30436d; }
div#header #main-nav > ul li:first-child { border-left: 0px solid #ecdbb8; }
div#header #main-nav > ul li:first-child:hover { border-left: 0px solid #3180ab; }
div#header #main-nav .sub-nav ul li:first-child { border: none; } 
div#header #main-nav > ul li.last { border-right: 0px solid #ecdbb8; }
div#header #main-nav > ul li.last:hover { border-right: 0px solid #3180ab; }

.entry-title {
    color: #009c94
}

a:link {
    -webkit-tap-highlight-color: #009c94;
}

.page #sidebar .second-menu ul li.active > a {
    -webkit-border-top-left-radius: 8px;
    -webkit-border-top-right-radius: 8px;
    -moz-border-radius-topright: 8px;
    -moz-border-radius-topleft: 8px;
    color: #acacac;
    font-weight: bold;
    display: block;
    margin: 0;
    padding: 8px 20px;
    text-shadow: 0px 0px 0px #333333;
}


.page #sidebar .second-menu ul ul.sub-pages li.last a {
    -webkit-border-bottom-left-radius: 8px;
    -webkit-border-bottom-right-radius: 8px;
    -moz-border-radius-bottomright: 8px;
    -moz-border-radius-bottomleft: 8px;
}

.page-title {
color: #009c94 !important;
}
.archives-log li a {
    color: #009c94 !important;
}
h1 {
    color: #009c94 !important;
}

span[style*="color: #333399"] {
  color: #009c94 !important; 
}

span[style*="color: #0000ff"] {
  color: #acacac !important; 
}

span[style*="color: #008000"] {
  color: #acacac !important; 
}

span[style*="color: #ff00ff"] {
  color: #acacac !important; 
}
span[style*="color: #ff00ff"] {
  color: #acacac !important; 
}
