  color: #009c94 !important; 
}
span[style*="color: #000080"] #text {
  color: #acacac !important; 
}

span[style*="color: #000000"] {
  color: #acacac !important; 
}
span[style="text-decoration: underline;color: #000080"] {
  color: #009c94 !important; 
}

span[style="color: #000080;text-decoration: underline"] {
  color: #009c94 !important; 
}
span[style="font-size: small;color: #000000"] {
  color: #009c94 !important; 
}

span[style*="color: #ff0000"] {
  color: #009c94 !important; 
}

span[style*="color: #333333"] {
  color: #acacac !important; 
}

span[style*="color: #ff6600"] {
  color: #009c94 !important; 
}

a[style*="color: #ff6600"] {
  color: #009c94 !important; 
}

a[style*="color: #333399"] {
  color: #009c94 !important; 
}

a[style*="color: #000080"] {
  color: #009c94 !important; 
}
a[style*="color: #000000"] {
  color: #acacac !important; 
}
a[style="text-decoration: underline;color: #000080"] {
  color: #009c94 !important; 
}

a[style="color: #000080;text-decoration: underline"] {
  color: #009c94 !important; 
}
a[style="font-size: small;color: #000000"] {
  color: #009c94 !important; 
}

a[style*="color: #ff0000"] {
  color: #009c94 !important; 
}



.page #sidebar .second-menu ul ul.sub-pages li a {
     background: #212224 !important;
    border-top: 1px solid #191a1b
}
ul.sub-pages li a:hover {
    background: rgb(0, 156, 148, 85)  url('http://protic-portail.csdecou.qc.ca/protic3/wp-content/themes/csdecouvreurs-secondaire/assets/images/layout/ico-arrow-menu.png') no-repeat 10px 13px;
}

#breadcrumb {
    color: #acacac !important;
}

#news article.headline .headline-holder {
    background: #212224;
    border-bottom: 0px #ecdbb8 solid;
}


#main-content #news article.headline .news-headline {
    position: relative;
    left: 5px;
    top: 25px;
    width: 260px;
    height: 100px;
}
.headline-holder.clearfix {
        margin-top: 18.5px;
        padding-top: 56px;
}
div#header {
    background: none;
    position: relative;
    z-index: 18;
    min-height: 175px;
    padding: 0 25px;
}

img {
    
        user-select: none;
        -moz-user-select: none;
        -khtml-user-select: none;
        -webkit-user-select: none;
        -o-user-select: none;
        -webkit-user-drag: none;
        -ms-user-select: none;

}
}
