css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.showstash.com/), url(http://showstash.com/), url(http://www.showstash.com/), url(http://showstash.com/) {

.style5, a[href="javascript:randomlink()"] { 
display: none !important;
}

.social_img { 
position: relative !important;
top: 8px !important;
left: 0px !important;
}

}

@-moz-document url-prefix(http://www.showstash.com/watch.php), url-prefix(http://showstash.com/watch.php) {

body table[style="border: medium none ; margin: 0pt; width: 100%; height: 100%; border-collapse: collapse;"]:first-child tr:last-child {
display: none !important;
}

}

@-moz-document url-prefix(http://www.showstash.com/shows.php), url-prefix(http://showstash.com/shows.php) {

td[width="44"] { display: none !important; }

td[align="left"][width="556"] { 
width: 100% !important;
}

table[style="border: 1px dashed rgb(143, 143, 143); background: rgb(89, 89, 89) none repeat scroll 0%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; color: rgb(255, 255, 255);"][border="0"][width="550"] { 
position: relative !important;
top: 10px !important;
}

.social_img { 
position: relative !important;
top: 4px !important;
left: 0px !important;
}

}
