css:
@-moz-document url-prefix("https://kuliah.ppkia.ac.id/") {

@font-face {font-family: "Poppins"; src: url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.eot"); src: url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.eot?#iefix") format("embedded-opentype"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.woff2") format("woff2"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.woff") format("woff"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.ttf") format("truetype"), url("//db.onlinewebfonts.com/t/510293e71666d65518569ae926af63f5.svg#Poppins") format("svg"); }

* {
    font-family: 'Poppins', sans-serif;
}

.bg-white {
    background-color: #4C566A!important;
}

.bg-dark {
    background-color: #4C566A!important;
}

.bg-light {
    background-color: #232629!important;
}

.border {
    border: 1px solid #2A2E32!important;
}

.border-radius {
    border-radius: 8px;
}

.alert-info {
    border-radius: 8px;
}

h1 {
    font-weight: bold;
}

body {
	background-color: #232629!important;
    color: #EFF0F1;
}

.badge-info {
    border-radius: 8px;
}

a {
    color: #BDC3C7;
    text-decoration: none;
} 

a:hover {
    color: #3DAEE9;
    text-decoration: none;
}

nav a {
    color: #BDC3C7;
}

nav a:hover {
    color: #BDC3C7;
}

.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
    color: #BDC3C7;
    background-color: #4C566A; 
}

.navbar-light .navbar-nav .show>.nav-link, .navbar-light .navbar-nav .active>.nav-link, .navbar-light .navbar-nav .nav-link.show, .navbar-light .navbar-nav .nav-link.active {
    color: #BDC3C7;
}

.navbar-light .navbar-nav .nav-link:hover, .navbar-light .navbar-nav .nav-link:focus {
    color: #BDC3C7;
}

.navbar-light .navbar-nav .nav-link {
    color: #BDC3C7;
}

.navbar-light .navbar-brand {
    color: #BDC3C7!important;
}

.navbar-light:hover .navbar-brand:hover {
    color: #3DAEE9!important;
}
                    
.fa {
    color: #BDC3C7;
}

.fa:hover {
    color: #3DAEE9;
}

.dropdown-menu {
    color: #262B35;
    background-color: #4C566A;
    border-radius: 12px;
}

.bg-gray {
    background-color: #4C566A!important;
}

a.bg-gray:hover, button.bg-gray:hover, a.bg-gray:focus, button.bg-gray:focus {
    background-color: #4C566A!important;
}

.btn-light {
    background-color: #2A2E32;
    border-color: #4C566A;
    border-radius: 8px;
}

.btn-light:hover {
    background-color: #1177d1;
    border-radius: 8px;
}

.btn-link {
    background-color: #4C566A;
    border-radius: 8px;
    color: #BDC3C7;
}

.btn-link:hover {
    background-color: #0b4f8a;
    color: #BDC3C7;
    text-decoration: none;
}

.btn-light:not(:disabled):not(.disabled):active, .btn-light:not(:disabled):not(.disabled).active, .show>.btn-light.dropdown-toggle {
    color: #262B35;
    border-color: #4C566A;
}

.dropdown-toggle:hover {
    color: #3DAEE9!important;
}

.btn-light:focus, .btn-light.focus {
    outline: 0;
}

.btn-primary {
    color: #BDC3C7;
    background-color: #4C566A;
    border-color: #232629;
    border-radius: 12px;
}

.btn-secondary {
    color: #EFF0F1;
    background-color: #1177d1;
    border-color: #232629;
    border-radius: 12px;
}

.btn-secondary:hover {
    border-color: #232629;
    background-color: #1177D1;
    color: #BDC3C7;
}

.block .block-cards .btn.btn-link.btn-icon:hover, .block .block-cards .btn.btn-link.btn-icon:focus {
    background-color: #232629;
}

.dropdown-item, .dropdown-item a {
    color: #BDC3C7;
}

.dropdown-item a:hover {
    color: #EFF0F1;
}

.dropdown-item.active, .dropdown-item:active {
    color: #BDC3C7;
    text-decoration: none;
    background-color: transparent;
}

.dropdown-item:hover {
    background-color: #3DAEE9;
    color: #262B35;
}

.dropdown-item:focus {
    background-color: #3DAEE9;
}

[data-region="drawer"] {
    background-color: #2A2E32;
}

.list-group-item {
    margin: 7px 0px;
    background-color: #232629;
    border-color: #2A2E32;
    border-radius: 12px;
}

.list-group-item.active {
    color: #BDC3C7;
    background-color: #4C566A;
}

.list-group-item-action {
    color: #BDC3C7;
}

.list-group-item-action:hover {
    background-color: #4C566A;
    color: #3DAEE9;
}

.list-group-item:hover {
    background-color: #4C566A;
    color: #BDC3C7;
}

.list-group-item:lost-focus {
    background-color: #4C566A;
    color: #BDC3C7;
}

.calendar_event_course {
    background-color: #4C566A;
    border-radius: 4px;
}

.path-calendar .maincalendar .calendar_event_course {
    border-color: #3DAEE9;
}

.card {
    background-color: #2A2E32;
    border-radius: 12px;
}

#region-main {
    background-color: #2A2E32;
    border-radius: 12px;
}

.courses .coursebox.even {
    background-color: rgba(0,0,0,.15);
    border-radius: 12px;
}

.custom-select {
    background-color: #4C566A;
    border: #2A2E32 1px;
    border-radius: 8px;
    color: #BDC3C7;
}

.btn-outline-secondary {
    border-radius: 8px;
}

.card-deck {
    background-color: #232629;
    padding-top: 8px;
    border-radius: 12px;
}

.card-img, .card-img-top, .card-img-bottom {
    border-radius: 12px;
}

.text-info {
    color: #BDC3C7!important;
}

.generaltable {
    color: #BDC3C7!important;
}

tr:hover {
    color: #BDC3C7!important;
}

.alert {
    border-radius: 8px;
}

.generaltable tbody tr:nth-of-type(odd) {
    background-color: rgba(0,0,0,.15);
}

.generaltable th, .generaltable td {
    border-top: 0;
}

.no-overflow a, .no-overflow a:hover {
	background-color: #2A2E32!important;
}

.form-control {
    border-color: #BDC3C7;
    border-radius: 8px;
}
.form-control, .form-control span, .editor_atto_notification .atto_info {
    background-color: #232629!important;
    color: #BDC3C7!important;
}

div.editor_atto_toolbar {
    background-color: #232629!important;
    border: 0;
    border-radius: 8px;
}

.editor_atto_content_wrap {
    background-color: #232629!important;
    border: 0;
}

.path-mod-assign td.submissionnotgraded, .path-mod-assign div.submissionnotgraded {
    color: #BDC3C7;
    background-color: #4C566A;
}

.db textarea, .db textarea:hover {
    color: #BDC3C7!important;
    background-color: #4C566A;
    font-size: 12px;
}

.progress-bar {
    background-color: #3DAEE9;
    border-radius: 4px;
}

.progress {
    border-radius: 4px;
}

.popover-region-container {
    background-color: #2A2E32;
    border: 0px;
    border-radius: 8px;
}

.popover-region-footer-container {
    background-color: #2A2E32;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

.popover-region-footer-container:hover {
    background-color: #3DAEE9;
    color: #BDC3C7;
    
}

.content-item-container.unread{
    background-color: #4C566A;
}

.content-item-container .content-item-footer .timestamp {
    color: #fff
}

.message-app {
    background-color: #2A2E32;
}

.input-group-text {
    border: 0px;
    border-radius: 8px;
    margin-right: 6px;
}

.popover {
    border-radius: 8px;
    border-color: #BDC3C7;
}

.popover-header {
    background-color: #4C566A;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}

.popover-body{
    background-color: #2A2E32;
    color: #BDC3C7;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

.card-body .text-truncate {
    color: #BDC3C7;
}

.text-truncate:hover {
    color: #3DAEE9!important;
}


.categoryname {
    color: #BDC3C7!important;
}

.coursename .text-truncate {
    color: #EFF0F1!important;
}

.coursename:hover, .coursename .text-truncate:hover {
    color: #3DAEE9!important;
}

.text-muted {
    color: #BDC3C7!important;
}

.header-button-title {
    color: #BDC3C7;
}

.header-button-title:hover {
    color: #3DAEE9;
}

.table {
    color: #BDC3C7;
}

.preferences-container .preference-table tr th:nth-child(even), .preferences-container .preference-table tr td:nth-child(even) {
    background-color: #2A2E32;
}

small.text-muted.text-truncate-mb-0:hover {
    color: #BDC3C7!important;
}

div.list-group-item.flex-column.py-2.pl-0.pr-0.border-0 {
    padding: 10px!important;
    margin: 0px 0px;
}

#page-footer a:hover {
    color: #3DAEE9;
}

::-webkit-scrollbar {
    width: 16px;
}
 
::-webkit-scrollbar-track {
    background-color: #232629;
    border-radius: 6px;
}
 
::-webkit-scrollbar-thumb {
    background-color: #4C566A;
    border-radius: 6px;
}

}
