css:
@-moz-document domain('blog.ir') {
:root {
	--color1: #1e1e1e;
	--theme-color: #00bdcf;
	--radius: 8px;
}
body {
	background: #f1f1f1;
}
* {
	max-width: 100% !important;
	box-sizing: border-box !important;
	font-family: sahel !important;
}
/*Remove Header Image*/
#header, #hdr {
	display: none;
}
/* maincontent */
#maincontent {
	background: transparent;
	padding-right: 190px;
	padding-top: 52px;
}
/*.dash*/
.ddash {
	height: 32px;
	background: var(--color1);
	color: #fff;
	display: flex;
	flex-direction: row-reverse;
	justify-content: space-between;
	position: fixed;
	top: 0;
	width: 100%;
	z-index: 3001;
}
.dash ul.left {
	display: flex;
}
.dash ul.left li {
	border: none;
}
.dash li a.dashLink {
	color: #fff;
}
.dash li.first {
	order: -1;
}
.dash li.first .red {
	font-size: 0;
	color: inherit;
}
.dash li.first .red:after {
	content: "\f011";
	font: normal normal normal 1rem/1 FontAwesome;
	display: inline-block;
	margin-left: 2px;
	color: inherit;
}
.dash li a.dashLink {
	color: inherit;
	padding: 2px 15px;
	display: block;
	height: 32px;
}
.dash li a.dashLink:hover {
	background: rgba(0, 0, 0, 0.5);
	color: var(--theme-color);
}
/*Tools menu*/
.tools {
	width: 170px;
	height: 100vh;
	margin-right: 0;
	position: fixed;
	top: 32px;
	right: 0;
	background: var(--color1);
}
#menu {
	width: 100%;
	padding: 0;
	background: transparent;
}
#menu ul {
	background: rgba(0, 0, 0, 0.5);
}
#menu li {
	background: transparent;
}
#menu li a {
	background: transparent;
	border: none;
	color: #fff;
	padding: 4px 12px;
	position: relative;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}
#menu ul li {
	border: none;
}
#menu ul li:first-child a {
	background: transparent;
	height: auto;
}
#menu ul li a {
	background: transparent;
	padding: 4px 12px;
	height: auto;
}
#menu ul li a:hover {
	background: transparent;
	color: var(--theme-color)
}
#menu li a::after {
	content: "";
	position: absolute;
	right: 0;
	top: 0;
	height: 100%;
	width: 4px;
	background: currentcolor;
	transform: translatex(100%)
}
#menu li a:hover::after {
	transform: translatex(0%);
	transition: transform 200ms ease;
}
#menu a.sel {
	background: var(--theme-color);
	color: #fff;
}
.dash .selectblog {
	width: auto;
	padding-left: 20px;
	opacity: .5;
}
.dash .selectblog:after {
	content: "انتخاب بلاگ";
	color: #fff;
}
.selectblog > img {
	display: none;
}
/*Field Item*/
.item.itemIn {
	border: 1px solid rgba(0, 0, 0, .1);
	box-shadow: 0 0 2px rgba(0, 0, 0, .1);
	background: #fff;
	border-radius: var(--radius);
}
.item > h2 {
	padding: 20px 8px;
	background: transparent;
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin: -8px;
	margin-bottom: 0;
}
.item > h2 .buttons {
	display: block !important;
}
.itemcontent {
	padding-top: 4px;
	border-top: 1px solid rgba(0, 0, 0, 0.1)
}
.buttons .button {
	display: none;
}
.buttons a {
	color: #888;
}

.buttons .close:before {
	content: "\f00d";
	font: normal normal normal 16px/1 FontAwesome;
	display: inline-block;
}
.buttons .move:before {
	content: "\f047";
	font: normal normal normal 16px/1 FontAwesome;
	display: inline-block;
}
.buttons .toggle:before {
	content: "\f068";
	font: normal normal normal 16px/1 FontAwesome;
	display: inline-block;
	vertical-align: middle;
	transition: transform 500ms cubic-bezier(1, 0, 0, 1);
}
.toggle.closed:before {
	content: "\f067";
	transform: rotate(180deg);
}
span.dashhelp {
	background: transparent;
}
span.dashhelp:before {
	content: "\f29c";
	font: normal normal normal 16px/1 FontAwesome;
	opacity: .3;
}

/*footer*/
div.mftr {
	width: 100%;
	margin: 0 auto;
	padding-right: 180px;
	display: flex;
	justify-content: space-between;
	flex-direction: row-reverse;
}
div.specialFtr {
	display: none;
}
/*انتشار*/
.grayTable {
	border: 1px solid #c3c4c7;
	box-shadow: 0 1px 1px rgba(0, 0, 0, .04);
	border-radius: var(--radius);
}
.grayTable thead,
.grayTable tfoot {
	background: #fff;
	color: #000;
}
.grayTable thead {
	box-shadow: inset 0 -1px #c3c4c7;
}
.grayTable tfoot {
	box-shadow: inset 0 1px #c3c4c7;
}
tbody {
	background: #f1f1f1;
}
/*Form*/
.topbuttons {
	margin-top: 0;
	height: auto;
}
.formField2 label {
	width: auto;
	float: none;
	margin-bottom: 4px;
	display: block;
	text-align: right;
}
.formField2 .fldcontent,
.field3 .formField2 .fldcontent {
	margin-right: 0;
}
.field3 .formField2 label {
	width: auto;
}
input[type="color"],
input[type="date"],
input[type="datetime-local"],
input[type="datetime"],
input[type="email"],
input[type="month"],
input[type="number"],
input[type="password"],
input[type="search"],
input[type="tel"],
input[type="text"],
input[type="time"],
input[type="url"],
input[type="week"],
select,
textarea {
	box-shadow: 0 0 0 transparent;
	border-radius: var(--radius);
	border: 1px solid #8c8f94;
	background-color: #fff;
	color: #2c3338;
}
input[type="submit"],
input[type="button"],
input[type="reset"],
a.btn,
a.btn:hover {
	background-image: none;
}
input[type].orangeBut {
	background-image: none;
}
a.btn {
	height: unset;
}
/*Tabs*/
ul#menua li a {
	padding: 10px;
}
/*قالبها*/
.template.normal {
	overflow: hidden;
}
.template.normal .viewTemImg,
.template .viewTemImg a {
	width: 100%;
}
img.tmplimage {
	width: 100%;
	height: auto;
}
.template.normal .buttons {
	transform: translateY(101%);
	transition: 250ms transform ease;
}
.template.selected .buttons,
.template.normal:hover .buttons {
	transform: translateY(0%);
}
input.savSet {
	background: #0C3 url(https://media/images/savSet.gif) no-repeat center top;
}
/*Media Query*/
@media (max-width: 1200px) {
	.field2,
	.field3,
	.field4,
	.field34,
	.field45,
	.field5 {
		width: 49.3%;
	}
}
@media (max-width: 1000px) {
	.field,
	.field2,
	.field3,
	.field4,
	.field34,
	.field45,
	.field5 {
		width: 100%;
	}
	.tools:not(.show) {
		transform: translatex(100%)
	}
	#maincontent {
		padding-right: 10px;
		padding-top: 42px;
	}
	#wrp {
		min-width: unset;
	}
}
#menu li a::before {
	font: normal normal normal 14px/1 FontAwesome;
	display: inline-block;
	margin-left: 10px;
}
#menu li a.publish:before {
	content: "\f040";
}
#menu li a.personal:before {
	content: "\f10e";
}
#menu li a.statistics:before {
	content: "\f080";
}
#menu li a.store:before {
	content: "\f0ed";
}
#menu li a.extension:before {
	content: "\f12e";
}
#menu li a.copy:before {
	content: "\f1f9";
}
#menu li a.facilities:before {
	content: "\f0ad";
}
#menu li a.setting:before {
	content: "\f013";
}
#menu li a.yourDoc:before {
	content: "\f2bd";
}
a[title$=" وبلاگ دنبال شده با مطلب جدید"] > img {
	display: none;
}
a[title$=" وبلاگ دنبال شده با مطلب جدید"]::after {
	content: "\f005";
	font: normal normal normal 14px/1 FontAwesome;
	display: inline-block;
	margin-left: 10px;
}
.fa {
	font: normal normal normal 14px/1 FontAwesome !important;
}

.note,
.noteInBut {
	background: transparent;
	color: #444;
}
.noteIn {
	background-image: none;
	box-shadow: 0px 10px 9px -12px rgba(0, 0, 0, 0.3);
	border-radius: var(--radius);
}
}
