css:
@-moz-document domain("metacpan.org") {
body {
    background-color:#2d2d2d;
    color: #c8c8c8;
}

a {
    color: #608cb3;
}
  
.navbar-default {
    background-color: #151515;
    border-color: #1e201f;
}

.alert-warning {
    color: lightgray;
    background-color: black;
    border-color: darkgray;
}

.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus {
    color: #ddd;
    background-color: black;
    font-weight: normal;
    box-shadow: none;
}

.active > a, .navbar .nav .active > a:hover {
    color: white;
    border-top-color: #1122aa;
    background-color: black;
    box-shadow: none;
}

.nav-list a, .nav-list .btn-link, .nav-list .nav-header {
    text-shadow: none;
}

.nav-header {
    text-shadow: none;
}

.form-control {
    background-color:#222;
    color:#eee;
    border: 1px solid #555;
    box-shadow: none;
}

.search-btn {
    background-color: #222;
    background-image: none;
    border-color: black;
    box-shadow: none;
    color: #444;
    text-shadow: none;
}

.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #1a1818;
}

.well {
    background-color: #212221;
    border-color: #0f0f0f;
    box-shadow: none;
}

.content .release-documentation div.release-row:nth-of-type(2n), .content .release-modules div.release-row:nth-of-type(2n), .content .release-provides div.release-row:nth-of-type(2n) {
    background-color: #313131;
}

.author-pic img {
    box-shadow: none;
}

code {
    background-color: #1e1e1e;
}

rect[fill="#e4e2e2"] {
    fill: #333;
}

.content {
    border-left-color: #222;   
}

.page-header {
    border-bottom-color: #222;
}

#index-container .index-border { border-color: #222 }

pre {
   border:none;
   background-color: #171717;
}

pre .syntaxhighlighter .plain, pre .syntaxhighlighter .plain a {
    color: lightgray !important;
}

pre .syntaxhighlighter .string, pre .syntaxhighlighter .string a {
    color: lightblue !important;
}
}
