css:
@-moz-document domain('notion.so'), url-prefix('https://www.notion.so/') {
  /* Remove in page mentions */
   .notion-page-mention-token {
      width: 2px;
  }
  /*makes page mentions have brackets [[page_name]]*/
   .notion-page-mention-token__title:before {
      content:"[[";
  }
  .notion-page-mention-token__title:after {
      content:"]]";
  }
  /*remove emojis in mentions*/
   .notion-page-mention-token span:nth-child(2) {
      display: none !important;
  }
  /* Remove left over page icons which didn't disappear from previous tweak */
   img.notion-emoji .page {
      display: none !important;
  }
}
