    font-weight: 400;
    color: #f8f9fa;
    white-space: nowrap;
    background-color: #666666;
    border: 0;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: 0.5rem 0;
    margin: 0.125rem 0 0;
    font-size: 1rem;
    color: #666666;
    text-align: left;
    list-style: none;
