css:
@namespace url(http://www.w3.org/1999/xhtml);

/** cs-manager.com only: **/
@-moz-document domain(cs-manager.com) {


/**
CSM-pink theme:

   The colour values used - search and replace these to change the color theme.


	Page backgrounds:
#EDFFEE; Very light pink for the non-content-part of the page.
#E1FFDE; Light pink background color for the content part of the page and highligted tabs.
#9BBA99; Very dark background. Used below left and right menus.


	Element backgrounds:
#C8FFBA; Pink background for elements on the page.
#97ED87; Darker pink background for emphasized elements.
#CEFFCC; Lighter pink bagground for larger textareas.
#E3FFE4; Very light background, used in forum.
#63BB5B; Pink background for headers


	Borders:
#B5D1B0; Very dark pink used for all sorts of borders
#B6D4B1; Dark pink used as border for headers to make them stand out
#C3E2BE; Light pink used as border for headers to make them stand out


	Text:
#E3FFE4; Standard text and menu links
#CEFFCC; Link colors (except for menus)


**/


body {	background: #D3FDD7 !important;  /** not really used as bg is white **/
}

a {
	color: #06470C !important;
/*	font-style: italic !important; */
}

/** Add remote logo as bg and change colors of header **/
#header {
	background-color: #71E971 !important;
	background-image: url("http://img49.imageshack.us/img49/1567/logopinkar9.png") !important;
	background-repeat: no-repeat !important;
	border-right: 1px solid #BDDEC1 !important;
	border-bottom: 1px solid #BDDEC1 !important;
	border-left: none;
	border-top: none;
}


/** Hide original logo: **/
#header img {	visibility: hidden !important;} /** ingame **/
#header a img {	visibility: hidden !important;} /** front page **/


/** box containing it all and a few elements, that shouldnt have any specific colour **/
#container, #footer_container,.post,#content {
	color: #003302 !important;
	background: #E2FFDE !important;
	border-right: 1px solid #C1DEBD !important;
	border-bottom: none; /** good manners to specify all borders **/
	border-left: none;
	border-top: none;
}

/** adding borders to some tables **/ 
table.rank,table.login {
	border: 1px solid #C1DEBD !important; 
}
/** adding background to some tables **/ 
table.rank,table.login,table.mail,table.info {
	background: #C0FFBA !important;
}
/** All sorts of 'small' elements needing colour **/
td.space,div.space {
	background: #CBF0CC !important;
	border:none !important;
}
a.player,td.player,td.top,td.tactic,a.tactic {
	background: #D4FFCC !important;
	color: #000000 !important;
}
a.tactic:hover {
	background: #C0ED87 !important;
}
td.position {
	background: #CDFFBA !important;
}

/** Adding borders and bg-colour to headers **/
td.head, h2, h3 {
	color: #000000 !important;
	background-color: #7AFF80 !important;
	background-image: none !important;
	border-top: 2px solid #45C52B !important;
	border-left: 2px solid #45C52B !important;
	border-right: 2px solid #9AF39A !important;
	border-bottom: 2px solid #9AF39A !important;
}

/** All sorts of links. (tabs, menu etc) **/
li a {
	background: #D3FFBA!important;
	color: #044704 !important;
	font-style: normal !important;
}

/** menu specific borders **/
ul#menu li a {
	border-bottom: 1px solid #BAE2AA !important;
	border-right: none !important;
	border-left: none !important;
	border-top: none !important;
}

/** tab-specific borders **/
ul#tab li a {
	border: 1px solid #C6DEBD !important;
}

li a:hover {
	background: #82E46F !important;
	font-style: normal !important;
}

/** Selected tabs **/
ul#tab li span {
	background: #E7FFDE !important;
	color: #143F05 !important;
	font-style: normal !important;
	border-bottom: #E7FFDE !important;
}

/** other spans (eg online in freinds list) **/
li span {
	color: #143F05 !important;
}

/** color of areas surrounding maillist **/
table.mail tr.head td, table.mail td.select, table.mail td.delete {
	background-color: #93ED87 !important;
}

/** dark area below the left and right menus **/
#menu, #footer_left, #footer_right, div#container>div#info {
	background: #92B983 !important;
}

/** search area **/
form#search {
	border-bottom: 1px solid #B2DDAE !important;
	background: #C0FFBA !important;
}

/**input fields and buttons **/
input,select,textarea,radio {
	background: D5DEAF !important;
	border: 1px solid #DDBBBB !important;
}
.buttonsmall, .button {
	background: #DBF8D5 !important;
	color: #0B4906 !important;
	border-top: 2px solid #33DE6A !important;
	border-left: 2px solid #33DE6A !important;
	border-right: 2px solid #B3EDA8 !important;
	border-bottom: 2px solid #B3EDA8 !important;
	font-size: 10px !important;
	padding: 0px !important;
}

/** news items (community news) **/
td.value {
	background: #C3D6C2 !important;
}
td.foot,td.info {
	background: #BAFFC2!important;
}

/** Training room colours **/
#training_map,#training_player,#skill,#trainer,#training_players {
	background: #BAFFC2!important;
}
div#training_type {
	background: #CCFFDA !important;
}

/** FORUM **/
tr.even {
	background: #E7FFE3 !important;
}
tr.odd {
	background: #C3D6C2 !important;
}
div.modify {
	background: #C3D6C2 !important;
}
}
