css:
@-moz-document url-prefix("https://www.dpreview.com") {
.imageViewer .filmstripContainer, .imageViewer .iconLights, .imageViewer .sidebar .social, .imageViewer .sidebar .tabs {
    display: none !important;
}
.imageViewer .toolbar {
    height:0 !important;
    padding: 0 !important;
    top: 24px;
    position: absolute;
    text-shadow: 1px 1px 2px black;
    z-index: 112;
}
.imageViewer .titleBar {
    height:0 !important;
    padding: 0 !important;
    width: 99%;
    margin-left:6px;
    top:6px;
    text-shadow: 1px 1px 1px black;
    position: absolute;
    z-index: 111;
    background-color: transparent !important;
}
.imageViewer .sidebar {
    padding: 0 !important;
    position: fixed;
    width:0 !important;
    text-shadow: 1px 1px 2px black;
    background-color: transparent !important;
}
.imageViewer .sidebar .adAndImageDetails {
    position: fixed;
    right: 28px;
	max-height: 100%;
    max-width: 18%;
    overflow-y: scroll;
    z-index: 109;
}
.imageViewer .next {
    z-index: 110;
 
}
}
