css:
@-moz-document regexp("https://vk.com/(?!ads).*") {
[scheme=vkcom_dark] {
    --steel_gray_10: var(--gray_800);
    --steel_gray_120: var(--gray_700);
    --button_secondary_background: var(--gray_800);
    --button_primary_background: var(--gray_800);
    --button_primary_foreground: var(--gray_100);
}
[dir=ltr] #side_bar_inner {
    position: fixed!important;
    margin-left: 0!important;
    margin-top: 42px!important;
    top: 0!important;
}
.friends_phonebook .friends_photo_stories_unseen .friends_photo_img,
.friends_phonebook .friends_photo,
.friends_phonebook .friends_photo_wrap,
.friends_phonebook .friends_photo_img,
.friends_photo_stories_unseen:after {
    width: 80px;
    height: 80px;
}
[dir=ltr] ::-webkit-scrollbar-thumb,
[dir=ltr] .mv_pl_scrollbar_cont.scrollbar_cont,
[dir] .ui_scroll_default_theme.ui_scroll_scrolled > .ui_scroll_bar_container > .ui_scroll_bar_outer > .ui_scroll_bar_inner,
[dir] .ui_scroll_default_theme > .ui_scroll_bar_container > .ui_scroll_bar_outer > .ui_scroll_bar_inner {
    height: 150px;
    background: var(--steel_gray_120)
}
[dir=ltr] ::-webkit-scrollbar-thumb:hover,
[dir] .ui_scroll_default_theme.ui_scroll_dragging > .ui_scroll_bar_outer > .ui_scroll_bar_inner,
[dir] .ui_scroll_default_theme .ui_scroll_bar_container:hover > .ui_scroll_bar_outer > .ui_scroll_bar_inner {
    background: var(--steel_gray_540);
}
[dir=ltr] ::-webkit-scrollbar {
    width: 10px;
    background: var(--steel_gray_10);
}
.ads_ads_box,
[dir] .search_filters_minimized_text,
#ads_left,
._ads_block_data_w,
.apps_feedRightAppsBlock,
[dir] .ads_ad_box.ver.repeat_ver.size_site.adaptive_ad {
    display: none!important;
}
a:hover {
    text-decoration: none;
}
}
