css:
@-moz-document url("https://cv.ucm.es/CampusVirtual/entrar.do") {
/*  Esto cambia los datos y la fecha  */
#header-box {
    background: rgb(56, 56, 61);
    color: white;
}

/* Esto cambia el menu INICIO-ESPACIOS-AYUDA */
#menu li a {
    color: white;
}
#menu li:hover a {
    background-color:rgb(56, 56, 61); 
    color: white;
}
/* Esto cambia los sub-menus */
#menu li {
    color: black;
    background-color: rgb(56, 56, 61);
}

/* Esto cambia el fondo de los avisos, pero no dentro de la caja de avisos */
div.avisos {
    background-color: rgb(56, 56, 61);
}

/*Esto cambia dentro de la caja de avisos, pero no los propios avisos*/
div.avisos .contenido{
    background-color: rgb(56, 56, 61);
}

div.avisos .encabezado{
    color: white;
}

#content-box{
    background-color: rgb(56, 56, 61);
}

body {
    background-color: rgb(56, 56, 61);
}

div.m{
    background-color: rgb(56, 56, 61);
}

/* Esto cambia el encabezado de cada despegable del curso del a√±o*/
.pane-sliders .panel h3{
    background-color: rgb(56, 56, 61);
}
/* Esto cambia la lista de asignaturas del despegable*/
table.adminlist thead th{
	background-color: rgb(56, 56, 61);
}
table.adminlist tbody th{
    background-color: rgb(56, 56, 61);
}
/* Esto cambia el boton de estado, el check verde ese */
.pane-sliders .adminlist td{
    background-color: rgb(56, 56, 61);
}
/* Esto cambia el panel derecho de iconos */
.cpanel div.icon a, #cpanel div.icon a{
     background-color: rgb(56, 56, 61);
    color: white;
}

/* Esto cambia el color del texto de las ASIGNATURAS */
a:link{
    color: white;
}
a:visited {
    color: white;
}


}
