css:
@-moz-document domain("finviz.com") {
/*COLORS*/
  :root {
      --black_bg: #181818;
      --dropdown: #27323A;
      --white: #cccccc;
      --highlights: #62697d;
      --yellow: #d6c586
  }
  .is-red {
      color: #ab4646;
  }
  .is-green {
      color: #4ea54e;
  }
  a.tab-link {
      color: #74afec;
  }

/* MARCH 11 UPDATE*/

.snapshot-td2, .snapshot-td2-cp {
    color: #838383;
}

.screener-link {
    color: #cccccc;
}


  /* custom dark theme */
  body,
  .header > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1),
  table.fullview-title {
      background-color: #0a091f;
      color: #ccc;
  }
  .body-combo-title, .screener-body-table-nw {
      color: var(--white);
  }
  /*////////////////////////////////////////////////////////////////////////////////////////////*/

  /*BACKGROUNDS*/
  body, .header > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1), table.fullview-title {
      background-color: var(--black_bg);
      color: #ccc;
  }

  table {
      background-color: var(--black_bg);
      background: none;
  }

  td {
      background-color: var(--black_bg);
  }

  /*SEARCHBAR*/
  .search > div:nth-child(1) > form:nth-child(1) > input:nth-child(1) {
      background-color: var(--dropdown);
      color: #eee;
  }

  /*NAVBAR*/

  /*PRESET TABLE*/
  .body-combo-text{
      background-color: var(--dropdown);
      border: none;
      color: var(--white);
  }
  .body-combo-text-h {
      background-color: var(--dropdown);
      border: none;
      color: var(--yellow);
  }

  .filter_inv {
      background: var(--highlights);
      color: var(--white);
  }
  .filter_y_inv {
      background: var(--highlights);
      color: var(--white);
  }


  /*FILTER TABLE HEADER*/


  .screener-tabs-selected, .screener-tabs-gray{
      background: none;
      background-color: var(--black_bg);
      font-size: 11px;
      padding: 5px;
      color: var(--white);
  }
  .screener-tabs-yellow, .screener-tabs-yellow-h, .screener-tabs-gray-h, .screener-tabs-selected-h{
      background: none;
      background-color: var(--highlights);
      font-size: 11px;
      padding: 5px;
      color: var(--white);
  }


  /*FILTERS*/

  td.filters-border {
      background-color: var(--black_bg);
      padding-top: 10px;
      padding-bottom: 30px;
  }

  td.screener-view, td.screener-view-a {
      border: none;
  }

  td.filters-cells > select,
  td.filters-cells {
      background-color: var(--black_bg);
      padding: 3px;
      border: none;
      color: var(--white);
  }
  .filters-cells > select.screener-combo-text {
      background-color: var(--dropdown);
  }
  .filters-cells > select.screener-combo-text-h {
      color: var(--yellow);
      background-color: var(--dropdown);
  }

  td.filters-cells > span {
      color: var(--white);
  }

  .screener-combo-button {
      background-color: #435055;
      color: var(--white);
  }


  /*VIEWS*/
  td.screener-view-a a {
      background-color: var(--highlights);
      color: var(--white);
      font-size: 12px;
      padding: 5px;
  }

  td.screener-view a {
      background-color: var(--black_bg);
      color: var(--white);
      font-size: 12px;
      padding: 5px;
  }

  td.screener-view, td.screener-view-a {
      background-image: none;
      background-color: var(--black_bg)
  }




  /*STOCK TABLE*/


  select {
      background-color: var(--black_bg);
      color: var(--white);
  }

  td.table-top, td.table-top-h  {
      color: var(--white);
      background-color: #3c3c3c;
  }

  td.table-top-s {
      color: var(--white);
      background-color: #27323A;
  }

  .pages-combo {
      background-color: var(--black_bg);
      color: var(--white)
  }

  /*BOTTOM PAGES*/
  .body-table {
      background-color: #3c3c3c;
  }







  /*///////////////////////////////////////////////////////////////////////////////////////////////*/
  /*TABLE*/

  .table-light-row-cp-h > td,
  .table-dark-row-cp-h > td {
      background-color: #3f668b;
      color: black;
  }
  a#ticker {
      background-color: transparent;
      color: white;
      font-size: 3rem;
  }
  tr.table-light-row-cp-h > td > a.tab-link,
  tr.table-light-row-cp-h > td > a.tab-link-nw {
      color: #4ea4ff;
  }

  /*
  tr.t-home-table-top > td {
      background-color: #333;
      color: #fff
  }
  */


  a.screener-pages:hover, a.screener-pages:link, a.screener-pages:visited {
      color: #9faab5;
      text-decoration: none;
  }

  table.t-home-table {
      background-color: #070615;
  }

  tr.nn-home-first > td,
  tr.nn-home > td {
      background-color: #070615;
      color: #eee;
  }

  tr.insider-option-row > td {
      background-color: #11222f;
      color: #eee;
  }
  tr.insider-sale-row-1 > td,
  tr.insider-sale-row-2 > td {
      background-color: #270404;
      color: #eee;
  }
  tr.insider-buy-row-1 > td,
  tr.insider-buy-row-2 > td {
      background-color: #042709;
      color: #eee;
  }

  td.snapshot-td,
  td.body-table-profile,
  tr.table-dark-row > td.snapshot-td2,
  table.body-table-news {
      background-color: #100f0c;
      color: #ccc;
      padding: 2px;
  }

  td.snapshot-td2-cp {
      background-color: #1d1b16;
      color: #eee;
      font-weight: bolder;
  }

  tr.calendar-now > td,
  tr.calendar-future > td,
  tr.table-dark-row-cp > td.screener-body-table-nw {
      background-color: #222;
  }
  tr.table-light-row-cp > td.screener-body-table-nw {
      background-color: #333;
  }
  tr.table-dark-row-cp > td.screener-body-table-nw > a.screener-link,
  tr.table-light-row-cp > td.screener-body-table-nw > a.screener-link {
      color: #ccc;
  }

  td.screener-body-table-nw > a.screener-link-primary {
      color: #647cd8
  }

  tr.body-table-rating-neutral > td {
      background-color: #060613;
      color: #eee;
      padding: 1px;
  }
  tr.body-table-rating-downgrade > td {
      background-color: #0e0202;
      padding: 1px;
  }
  tr.body-table-rating-upgrade > td {
      background-color: #020e03;
      padding: 1px;
  }
  table.fullview-news-outer {
      background-color: rgba(255, 255, 255, 0.05);
  }
  table.fullview-news-outer > tr > td {
      padding: 2px;
  }
  td.fullview-profile {
      color: yellow;
      background: black;
      border-color: black;
      font-style: italic;
      font-size: 1rem;
  }
  .fullview-title > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > a:nth-child(1) > b:nth-child(1) {
      color: white;
  }

  td.snapshot-td,
  td.snapshot-td2,
  td.snapshot-td2-cp {
      border-color: #545454;
      padding: 4px;
  }
  tr.table-light-row-cp > td {
      background-color: #0a091f;
  }
  tr.nn-h > td {
      background-color: #333;
      color: #eee;
  }
  .screener-body-table-nw {
      padding: 2px 0px;
  }
  #homepage > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr > td {
      background-color: #17162e;
      padding:10px 0px;
  }
  /* image invert */
  #homepage > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td > img,
  #homepage > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td > img,
  img#msb_chart,
  img#chart0,
  #screener-content > table:nth-child(1) > tbody:nth-child(1) > tr  > td > table  > tbody  > tr > td > a > img,
  img.charts-gal {
      filter:hue-rotate(180deg) invert(100%);
      -webkit-filter:hue-rotate(180deg) invert(100%);
  }
  #screener-content > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(5) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
      background-color:black;
  }

  /* remove adds */
  div#banner_position {
      opacity: 0;
  }
  #homepage > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(6) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) {
      opacity: 0;
  }
}
