css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("games.espn.go.com") {

/* ESPN Fantasy Games Custom Edits by KonphliKt v. 0.1*/

/* Changing Some Colors */

#playerTableHeader li {
background: #6FB8FF !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

#playerTableHeader #current {
background: #9FCFFF !important;
}

.playertableSectionHeader, .playertableTableHeader {
background: #084C8F !important;
}

.playerTableBgRowSubhead td, .playerTableBgRowSubhead2 td, .tableSubHead {
background: #6FB8FF !important;
}

div.chOwnerCallouts, .chtop {
background: #9fcfff !important;
}

.chbtm {
background: #084c8f !important;
color: #eeeeee !important;
height: 60px !important;
-moz-border-radius: 5px !important;
}

.chbtm ul {
color: #eeeeee !important;
}

.transDivLabel  {
background-color: #6fb8ff !important;
}

body a:hover, .anchorStyle:hover {
color: #121212 !important;
}

body a:link, .anchorStyle {
color: #121212 !important;
}

.fantasynav, .hdrteamstxt, .fantasynavbg {
background-color: #084c8f !important;
}

.fantasynavbg {
background: #084c8f !important;
}

.hdrteamstxt {
width: 110px !important;
margin-left: 150px !important;
text-align: right !important;
}

body {
background: #121212 !important;
}

body.trans-fantasy .head {
background: #6fb8ff !important;
}

.fantasynavtab a {
-moz-background-clip:border !important;
-moz-background-inline-policy: continuous !important;
-moz-background-origin: padding !important;
display:block !important;
height:31px !important;
margin:0 2px 0 0 !important;

}

/* Change Color of Lineups Date Links - 12/4/09 */

.lineupsNav div:hover {
color: #121212 !important;
font-weight: bold !important;
}

/* Change Default Navigation Tabs to Custom Tabs */

.myteamtabon a:link, .myteamtab a {
width: 70px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/teamdb.png) repeat scroll 0 0 !important;
}

.myteamtabon a:hover, .myteamtab a:hover, .myteamtabon a {
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/teamlb.png) repeat scroll 0 0 !important;
width: 70px !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.leaguetab a, .leaguetab a:link {
width: 60px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/leaguedb.png) repeat scroll 0 0 !important;
}

.leaguetab a:hover, .leaguetabon a:hover, .leaguetabon a {
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/leaguelb.png) repeat scroll 0 0 !important;
width: 60px !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.playerstab a, .playerstab a:link {
width: 75px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/playersdb.png) repeat scroll 0 0 !important;
}

.playerstab a:hover, .playerstabon a:hover, .playerstabon a {
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/playerslb.png) repeat scroll 0 0 !important;
width: 75px !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.standingstab a, .standingstab a:link {
width: 85px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/standingsdb.png) repeat scroll 0 0 !important;
}

.standingstab a:hover, .standingstabon a:hover, .standingstabon a {
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/standingslb.png) repeat scroll 0 0 !important;
width: 85px !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.scoreboardtab a, .scoreboardtab a:link {
width: 102px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/boarddb.png) repeat scroll 0 0 !important;
}

.scoreboardtab a:hover, .scoreboardtabon a:hover, .scoreboardtabon a {
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/boardlb.png) repeat scroll 0 0 !important;
width: 102px !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.teamstab a, .teamstab a:link {
width: 80px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/teamslb.png) repeat scroll 0 0 !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.teamstab a:hover {
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/teamslb.png) repeat scroll 0 0 !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.lmtoolstab a, .lmtoolstab a:link {
width: 78px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/lmdb.png) repeat scroll 0 0 !important;
}

.lmtoolstab a:hover, .lmtoolstabon a:hover, .lmtoolstabon a {
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/lmlb.png) repeat scroll 0 0 !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.fantasycasttab a, .fantasycasttab a:link {
width: 150px !important;
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/fantasyno.png) repeat scroll 0 0 !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.fantasycasttab a:hover, .fantasycasttabcaster a, .fantasycasttabcaster a:link, .fantasycasttabcaster a:hover {   /* When Fantasy Cast is 'Turned On' by ESPN, This Will Have Bright Text */
background: transparent url(http://i663.photobucket.com/albums/uu355/konphlikt/fantasylive.png) repeat scroll 0 0 !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

/* Change Team Highlighter */

.navddmenuitemhighlight {
background: #6fb8ff !important;
}

/* Change Colors on PopUp Boxes */

.ffl #pcNewsPlayerName, .ffl #pcClose {
background-color: #084c8f !important;
}

/* Get Rid of the Footer */

body.trans-fantasy #trans-footer.container {
display: none !important;
}

/* Front Page "League" Changes */

.hBlue {
background: #084c8f !important;
-moz-border-radius-topleft: 6px !important;
-moz-border-radius-topright: 6px !important;
}

.modRnd, .modBgrad, .whiteparentcontainer, body.trans-fantasy #bannercontainer, .tableHead, #pcName td, #pcHV {
background: #084c8f !important;
}

.subnavcontaineron {
background: #6fb8ff !important;
-moz-border-radius: 4px !important;
}

.hBrown {
background: #6fb8ff !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

/* Misc Color Changes */

.sortedCell, .slotChanged, #pcWeek {
background: #6fb8ff !important;
}

/* tr:hover Changes Hover Row Color */

tr:hover, .whitecontainer, .clubhousemodule, .lineupsNavItemOn {
background-color: #9fcfff !important;
}

/* Color Change of Spacer */

.sectionLeadingSpacer, .ctColPad {
background-color: #084c8f !important;
}

.ctColPad {
-moz-border-radius: 5px !important;
}

/* Border Change Color */

.playertableFrameBorder {
border: 8px solid #084c8f !important;
-moz-border-radius: 5px !important;
}

/* Other Popup Color Changes */

.lineupsNav, .colorNote {
background: #6fb8ff !important;
-moz-border-radius: 5px !important;
}

/* Message Board Tweaks - Added 12/3/09 */

.bg0 {
background: #CFE7FF url(http://g.espncdn.com/s/ffllm/09/images/messageboard/bg_post.gif) !important;
border: 1px solid #121212 !important;
}

.bg1 {
background: #8FC7FF  url(http://g.espncdn.com/s/ffllm/09/images/messageboard/bg_post.gif)!important;
border: 1px solid #121212 !important;

}

.bg0:hover, .bg1:hover {
background-color: #AFD7FF !important;
}

.post-body-header, .postDate {
color: #131313 !important;
}

/* Different Move, Here, IR, Drop, etc Buttons - Uncomment if you dont like or replace with your own - Added 12/3/09 */

.pncButtonMove, .pncButtonMoveSelected, .pncButton {
background: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/move2.png) no-repeat scroll 0 50% !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
}

.pncButtonHere {
background-image: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/here2.png) !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
}

.pncButtonDropped {
background-image: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/dropped2.png) !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
}

.pncButtonDrop, .pncButtonDrop2 {
background-image: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/drop2.png) !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
}

.pncButtonUndroppable, .pncUndroppable2 {
background-image: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/cantdrop2.png) !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
cursor: crosshair !important;
}

.pncButtonLocked {
background-image: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/locked2.png) !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
cursor: crosshair !important;
}

.pncButtonActivate, .pncButtonActivate2 {
background-image: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/activate.png) !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
}

.pncButtonToIR, .pncButtonToDL, .pncButtonToDL2 {
background-image: url(http://i605.photobucket.com/albums/tt139/ArtificialPain/irdl2.png) !important;
-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-topright: 9px !important;
}

.pncButtonToIR:hover, .pncButtonToDL:hover, .pncButtonToDL2:hover, .pncButtonActivate:hover, .pncButtonActivate2:hover, .pncButtonLocked:hover, .pncButtonUndroppable:hover, .pncUndroppable2:hover, .pncButtonDrop:hover, .pncButtonDrop2:hover, 
.pncButtonDropped:hover, .pncButtonHere:hover, .pncButtonMove:hover, .pncButtonMoveSelected:hover, .pncButton:hover, .clubhouseteamlogo:hover {
-moz-box-shadow: 2px 8px 8px rgba(0,0,0,0.625) !important;
}

/* Player PopUps Page -- 1/15/10 -- */
#pcEven:hover {
background-color: #9fcfff !important;
}

#pcOdd:hover {
background-color: #7FBFFF !important;
}

.fba #pcNewsPlayerName, .fba #pcClose, .fba .pcStatMainHead {
background-color: #084c8f !important;
}

th.pcTanRight {
background-color: #9fcfff !important;
}

/* Change Text of Vote Buttons and Drop Down list of VS Games - Changed due to theme I am using // Uncomment if Needed // */

.sidebar {
color: #000 !important;
background-color: #777 !important;
}

/* Rounded Corners - 12/8/09 */

.brownModBg, .modRnd {
-moz-border-radius-bottomright: 5px !important;
-moz-border-radius-bottomleft: 5px !important;
}

/* Change background when ESPN 'pops up' score */

body.trans-fantasy #coverDiv, body.trans-fantasy {
background: #131313 !important;
}

/* ESPN 'Pop Up' Score Updater */

/*#toastDiv[id="toastDiv"], #toastDiv {
to be continued  
}*/

/* For Use with Chatbox in Our League -- Personal Theme on Win 7 Conflicts -- You can uncomment if needed */
.frmtb {
background: #777 !important;
color: #131313 !important;
}
}
