css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://apps.facebook.com/countrystory") {
	.UIStandardFrame_Container {
		width: 500px !important;
		padding-left: 0 !important;
	}
	.UIStandardFrame_SidebarAds,
	.app_content_165747315075 div#app165747315075_sidebar,
	.app_content_165747315075 style+script+div,
	.app_content_165747315075 p{
		display:none !important;
	}
	.UIStandardFrame_Content {
		width: 500px !important;
	}
	.app_content_165747315075 div#app165747315075_sidebar + div,
	.app_content_165747315075 div#app165747315075_sidebar + div iframe{
		width: 500px !important;
		height: 550px !important;
	}
}

@-moz-document url-prefix("http://farm.game.playfish.com/g/fb/farm") {
	body {
		margin: 0 !important;
	}
	embed {
		width: 500px !important;
		height: 550px !important;
	}
}
