css:
@-moz-document url-prefix("https://www.hoyolab.com/") {
body {
	background: #1b1b1b;
	color:#acbace;
}
.mhy-article-card, .mhy-side-menu, .mhy-side-section, .header__wrp, .mhy-post-new, .mhy-page-header-wrp, .mhy-account-center-header, .mhy-container, .mhy-dialog__content, .mhy-contribution-card, .mhy-strategy-list, .mhy-float-dialog, .mhy-reply-list, .mhy-emoticon__bottom {
	background-color: #1f2022;
}
.header {
	padding-bottom: 0px;
}
.mhy-page-header-wrp {
	border-radius:0px
}
.mhy-page-header--top .mhy-page-header-wrp {
	border-radius: 0;
}
.mhy-article-card+.mhy-article-card {
	border-top: 0px solid #f1f4f9;
}
.mhy-game-record-card__value, .mhy-account-center-header__data-num, .mhy-account-title__name, .mhy-op-card__title, .mhy-topic-card__name, .mhy-topic-card__header, .mhy-recommend-user-list-side, .mhy-side-menu__item, .mhy-article-card__title>span:last-child, .mhy-button-normal .mhy-button__button, .mhy-article-card__data-item .mhy-icon, .mhy-article-card__data-item .mhy-symbol-icon, .mhy-article-card__data-item .mhy-img-icon, .mhy-follow-button:hover, .mhy-game-record-card__name, .header__logo .mhy-icon, .ql-editor, .mhy-post-new__post span, .mhy-contribution-card__title, .mhy-strategy-card__title h2 {
	color:#5299ff;
}
.mhy-avatar__img {
	border: 0px dotted #fff;
}
.mhy-recommend-user-list-side .mhy-user-card:hover, .mhy-op-card:hover, .mhy-topic-list .mhy-topic-card:hover, .mhy-side-menu__item:hover, .mhy-game-record-card__item, .mhy-follow-button--follow, .mhy-button-normal .mhy-button__button, .mhy-follow-button, .reply-card__replies, .mhy-button-header .mhy-button__button, .mhy-contribution-card__time, .header-account-menu-item__content:hover, .header-account-menu-item--active .header-account-menu-item__content, .mhy-emoticon__list, .mhy-emoticon__set--selected {
	background-color: #383838;
}
.mhy-page-header--top .mhy-page-header-content {
	border-bottom: 0px solid #f1f4f9;
}
.mhy-topic-card__stats, .mhy-game-record-card__title, .reply-card__content, .reply-card .reply-card-inner-reply__content, .mhy-dialog__title, .s-reply-list__title, .mhy-contribution-card__desc, .mhy-strategy-card .mhy-user-card:not(.mhy-user-card--deleted) .mhy-account-title__name, .mhy-img-text-article.ql-snow .ql-editor p, .mhy-simple-editor .ql-snow .ql-editor p {
	color:#acbace;
}
.mhy-side-menu__icon {
	display:none
}
.mhy-reply-box__textarea .reply-editor .ql-editor, .mhy-strategy-list__header, .mhy-strategy-card+.mhy-strategy-card {
	border: 0px;
}
}
