css:
@-moz-document url-prefix("https://podio.com") {
/* header grey instead of teal */
#header-global { background: #c2d1d0; }
.space-switcher-wrapper.highlight { background: #c2d1d0; }
#header-global .nav-box>.inner .user-functions { background: #c2d1d0; }
#header-global .podio-search-box { background: #c2d1d0; }
/* headers and buttons less noisy */
.button-new { border-radius: 0 !important; padding: 0 10px; min-width: unset; margin: 0 !important; font-weight: lighter; }
.button-new.secondary { background: #eee !important; box-shadow: inset 0 0 0 1px #ddd !important; color: #555 !important; }
.action-dropdown-v2 .action-trigger { margin-top: 3px; }
.header-title { font-weight: lighter; color: #05758A; }
.header-title a { color: #05758A !important; }
#space-navigation ul.app-nav li.core.app-apps a { filter: grayscale(1); opacity: 0.4; }
/* notifications more noisy */
#header-global div.icon-notifications-with-count + div { background: yellow; color: red !important; border-radius: 15px; min-width: 1em; }
#header-global li.podio-chat div.slot-machine-counter .count { background: yellow; color: red !important; border-radius: 15px; min-width: 1em; margin-left: 0; }
/* tile images fix */
.dashboard .gridster-block .image .image-container img { max-width: 95%; }

}
