css:
@media(prefers-color-scheme: dark) {
    body {
        background-color: #730023;
        color: #f3f3f3;
    }
    a {
        color: #b6043a;
    }
    h3 {
        color: #ababab;
    }
    #container {
        background-color: #202020;
    }
    #logo img {
        filter: brightness(1.1);
    }
    #global_search input[type="text"] {
        background-color: #202020;
        color: #fff;
    }
    #navi li {
        background-color: #202020;
        color: #fff;
    }
    #navi a {
        color: #ababab;
    }
    #navi .subnavi {
        background-color: #202020;
    }
    #navi ul.sub a {
        color: #ababab;
    }
    #navi .subnavi a:hover {
        background: #2c2c2c;
    }
    #breadcrumble {
        color: #ababab;
    }
    #breadcrumble span.active_site {
        color: #c8c8c8;
    }
    #breadcrumble a:hover {
        color: #ababab;
    }
    #content .content-middle {
        color: #888888;
    }
    .cc-license p a {
        color: #9e9e9e;
    }
    .cc-license p {
        color: #696969;
    }
    .news_section_headline, .media > h2 {
        color: #c6c6c6;
    }
    .news-slider .news-item p, #portlet_video > div > p {
        color: #ababab;
    }
    .start_news h3, .two_news h3, .three_news h3, .event h3 {
        color: #ababab;
    }
    .rating span:first-child {
        color: #ababab;
    }
    .portlet_study_trip_content p {
        color: #ababab;
    }
    .portlet_study_trip_content h4 a {
        color: #ababab;
    }
    #portlet_navi ul li svg path {
        stroke: #a8a8a8;
    }
    .portlet .portlet_headline {
        color: #a7abab;
    }
    #portlet_navi a {
        color: #a8a8a8;
    }
    #portlet_navi ul li a:hover {
        background: #2c2c2c;
    }
    .portlet_tag_content p {
        color: #ababab;
    }
    #content_left {
        color: #ababab;
        background-color: #202020;
        border-right-color: #ababab;
    }
    .bpbsourcecode {
        background-color: #2c2c2c;
    }
    .bpbsourcecode span.bpbsourcecode_type {
        color: #b9b9b9;
    }
    .bpbsourcecode div.p {
        color: #ababab;
    }
    .start_news h2 a, .two_news h2 a, .three_news h2 a, .event h2 a {
        color: #888888;
    }
    .start_news p, .two_news p, .three_news p, .event p {
        color: #ababab;
    }
    article {
        color: #ababab;
    }
    article .pub_date {
        color: #ababab;
    }
    .article_image_right span {
        color: #ababab;
    }
    #footer {
        background-color: #202020;
    }
    #footer_lists {
        border-bottom-color: #202020;
    }
    #footer_lists ul li:first-child {
        color: #777777;
    }
    #footer_lists li {
        color: #7e7e7e;
    }
    #footer .socialmedia p, #footer .partners p {
        color: #ababab;
    }
    #footer .partners ul li a {
        color: #ababab;
    }
    [style*="color:#000000;"] {
        color: #ababab !important;
    }
    .site-actions, .site-actions a {
        color: #ababab;
    }
    .price {
        color: #fff;
        background-color: #005f85;
    }
}
