css:
@-moz-document domain("my.workfront.com") {
    tr:nth-child(even) {
        background-color: #EEE;
    }

    #content-timesheet-view .wkend {
        background-image: initial;
        background-color: #F4F4F4;
    }

    #content-timesheet-view tr:nth-child(even) .wkend {
        background-color: #E6E6E6;
    }

    #content-timesheet-view tr:nth-child(even) .wkday {
        background-color: #F2F2F2;
    }

    #content-timesheet-view .today {
        background-image: initial;
    }

    #content-timesheet-view tr:nth-child(even) .today {
        background-color: #DBEAF4;
    }

    #content-timesheet-view tr:nth-child(even) .total, #content-timesheet-view tr:nth-child(even) .spacer {
        background-color: #E5F4FF;
    }
}
