css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.linux.org.ru/) {
body {
	background: #515457 !important;
	color: #fff !important;
	font-size: 12px !important;
	padding: 0 !important;
        font-family:	"Bitstream Vera Sans", Helvetica, Verdana,  sans-serif !important;
        line-height: 1.5 !important;
}
h1 {font-size: 16px !important}
h2 {font-size: 13px !important}
h2 a {
	color: #fff !important
}
h2 a:hover {
	text-decoration: underline !important;
}
body div p {
	color: #fff !important;
	font-size: 12px !important;
}
a {
	color: #ccc !important;
	text-decoration: none !important;
}
a:hover {
	color: #ccc !important;
	text-decoration: underline !important;
}
table + table {display:none !important}
table {border:none !important;}
body img {display: none !important}
body > h1 {display: none !important}
div[align="center"] p {margin-left: 220px !important}
.boxlet {
	background: #555 !important;
	border: dotted 1px #222 !important;
	font-size: 11px !important; 
	margin: 0 0 4px 0 !important;
	text-align: left !important; 
	padding: 4px !important;
	width: 220px !important;
}
.boxlet h2 {
	display: block !important;
	background-color: #505050 !important;
	border-color: #222 !important;
	border-width:0 0 1px 0 !important;
	border-style:dotted !important;
	color: #fff !important;
	font-size: 14px !important;
	margin: -4px 0 0 -4px !important;
	padding: 0 4px !important;
	width: 100% !important;
}
.boxlet h3 {
	font-size: 12px !important;
	margin-top: 2px !important;
	margin-bottom: 3px !important
}
.boxlet form {margin: 0 !important}
.boxlet form img {display:inline !important}
.boxlet form p {margin: 0 !important}
.boxlet a img {
	display: block !important;
	border: none !important
}
.boxlet a {color: #ccc !important}
.boxlet a:hover {
	text-decoration: underline !important;
}
.boxlet i, .boxlet p i {
	color: #ccc !important;
	font-style: normal !important;
}
.boxlet br {line-height: 0.1em !important}
.boxlet iframe {width: 220px !important}

.newsblog-in {margin-left: 238px !important}
.newsblog h1 {display: none !important}
.newsblog .nav {
	border: none !important;
}
.news {
	background-color: #545751 !important;
	border:1px dotted #222 !important;
	margin: 0 0 5px 0 !important;
	padding: 7px !important;
}
.news h2 {
	display: block !important;
	background-color: #505050 !important;
	text-align: left !important;
	border-bottom: 1px dotted #222 !important;
	padding: 2px 5px !important;
	margin: -7px -7px 0 -7px !important;
}
.entry-body {
	margin: 0 0 0 2em !important;
	padding: 5px 0 0 0 !important;
}
.entry-body .msg {
	border: none !important;
}
.entry-body .msg  i {
	display: inline !important;
}
.entry-body .nav {
	display: block !important;
	background-color: #545751 !important;
	border-color: #222 !important;
	border-width:1px 0 0 1px !important;
	border-style:dotted !important;
	margin: 0 -7px -7px 30% !important;
	padding: 2px !important;
	padding-left: 5px !important;
	padding-right: 5px !important;
	font-size: 11px !important;
}
.sign {font-size: 11px !important; color: #ccc !important}
.sign a {color: #ccc !important}
.sign 

.head-main table {
	font-size: 11px !important;
	border: none !important
}
.head-main table td {
	padding: 0 1em !important;
	border: none !important
}
.nav {
	background-color: #505050 !important
}
.messages {
	background-color: #515457 !important;
	color: #fff !important;
	font-size: 12px !important;
	margin: 5px 0 !important;
}
.messages h1 {display:none !important}
.title {
	background-color: #505050 !important;
	color: #aaa !important;
	font-size: 10px !important;
        line-height:16px;
	padding: 0px !important;
	padding-left: 5px !important;
        margin:0px  !important;
        height:18px !important;
}
.title a {color: #aaa !important}
.title a:hover {text-decoration: underline !important;}
.userpic {padding: 2px !important}
.msg {
	background-color: #545751 !important;
	border: dotted 1px #222 !important;
	color: #fff !important;
	font-size: 12px !important;
	margin: 0.4em 0 !important;
	padding: 0 !important;
}
.msg h2 {
	background-color: #505050 !important;
	margin-top: 0 !important;
	padding: 3px !important
}
.msg_body {
	background-color: #545751 !important;
	color: #fff !important;
}
.msg_body h2 {
        display:none;
	margin: 0 -0.5em 4px -0.5em !important;
}
.msg img {
	border: none !important;
	display: inline !important;
}
.msg i {
	background-color: #505050 !important;
        border:1px dotted #858585;
	color:#fff !important;
	display:block !important;
	margin: 5px 0 !important;
	padding: 3px 5px !important;
}
.msg p a {
	color: #ccc !important
}
.msg p a:hover {text-decoration: underline !important;}
div.forum table thead, div.forum table tfoot tr, div.forum table th {
	background-color: #505050 !important
}
div.forum table tbody tr {
	background-color: #545751 !important
}
div.forum table td {
	padding: 0.1em 0.5em !important
}
div.forum table tbody tr:hover {
	background-color: #505050 !important
}

#ibmdw {display:none !important}
#navPath {padding-left:15px !important}
.quote {	
	background-color: #505050 !important;
        border:1px dotted #858585 !important;
	color:#fff !important;
	display:block !important;
	margin: 5px 0 !important;
	padding: 3px 5px !important;}
.quote h3 {font-size:10px !important;display:none !important;}
.quote p {
	margin:0px !important;
	padding:0px !important;}
.quote p:before {content: ">"}
.tags {
        background-color: #505050 !important;
        border:1px dotted #858585 !important;
	color:#fff !important;
	display:block !important;
	margin: 5px 0 !important;
	padding: 3px 5px !important;}

}

@-moz-document url-prefix(http://www.linux.org.ru/gallery/) {body img {display: block !important}}
