css:
@-moz-document domain(psdqatar.com) {
/*Created By: Aaron*/
html{
	scroll-behavior: smooth;
} 
::selection {
  background: #313131cf;
  color: white;
}
::-moz-selection {
  background: #a8fcff; /* Gecko Browsers */
}
 * {
  margin: 0px;
  width: auto;
  left: auto;
  right: auto;
  top: auto;
  bottom: auto;
}

::-webkit-scrollbar {
  width: 12px;
}

::-webkit-scrollbar-track {
      background: rgb(99 99 99);
}
  
  .showmore {
  	color: FFFFF;
  }

::-webkit-scrollbar-thumb {
background: #acacac;
    border: 3px solid rgb(99 99 99);
    border-radius: 10px;
}

body {
	background-color: #a2a2a2 !important;
}

.head {
	background-color: #ececec !important;
}

.menu {
	background-color: #ececec !important;
}

.info img {
	border: 1px solid rgb(135 135 135);
}

.myinfohead{
	background-color: #d5d5d5 !important;
}
  
  .myinfohead p {
  	    color: rgb(0 0 0 / 90%)
  }

.myinfo{
	background-color: #c9c9c9 !important;
}

  #adv a {
  	color: rgb(66 66 66) !important;
  }
  
#adv a:hover{
		    color: rgb(0 0 0);
}

#uname a {
	color: #FFFFFF !important;
}
  
  #phil {
  	color: #181818;
  }
  
  #name {
  	color: #000000;
  }
  
  #uname {
  	color: #323232;
  }
  
  #uname a {
  	color: #373737;
  }
  
#uname a:hover{
	color: wheat;
	letter-spacing: 1px;
	border-bottom: 1px solid #737373;
}

#tamsoy{
		color:rgba(220,220,220,0.9);
}

#active{
	color: rgb(0 0 0);
    border-bottom: 2px solid rgb(0 0 0);
    font-weight: bold;
}

.links a:hover{
	color: rgb(207 199 145);
    border-bottom: 2px solid rgb(207 195 145);
}
  
  .links a{
	color: #594e4e;
}

#subtitle{
	color: rgb(255 255 255);
	color: rgb(242 239 215);
}

#periodicallink{
  	color: #ffffff;
}

#room {
	color: #66635e;
}

.forcomment input[type=submit]:hover{
	background:rgb(255 255 255);
	border:1px solid rgb(0 0 0);
	color:rgb(0 0 0);
	border-radius:0px 0px 0px 0px;
}

#viewall:hover{
color:#615b52;
	text-decoration:underline;
	transition:0.3s;
}

#pname{
	padding-left:10px;
	font-size:11px;
  color: white;
}
#pdate{
	padding-left:10px;	
	font-size:11px;
  color: white;
}
#ptime{
	padding-left:5px;
	font-size:11px;
  color: white;
}

.accordion {
		background: rgb(146 146 146);
 		color: #3e3e3e;
}
  
  .accordion:hover{
  	background: rgb(41 41 41);
    color: rgb(255 255 255);
    border: 1px solid rgb(255 255 255);
  }
  
 
  
 .videolinks a:hover{
 		border: 0.5px solid rgb(165 234 247);
   		color: rgb(165 234 247);
 }
  
  .post p {
  		color: #ffffff !important;
  }
  
.active, .accordion:hover {
    background: rgb(41 41 41);
    color: rgb(255 255 255);
    border: 1px solid rgb(255 255 255);
}
  
  .panel{
    border-right: 1px solid rgb(255 255 255) !important;
    border-bottom: 1px solid rgb(255 255 255) !important;
    border-left: 1px solid rgb(255 255 255) !important;
}

 
  ::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #000000;
  opacity: 1; /* Firefox */
}

:-ms-input-placeholder { /* Internet Explorer 10-11 */
  color: #000000;
}

::-ms-input-placeholder { /* Microsoft Edge */
  color: #000000;
}
  
  .top {
    width: 100%;
    height: 30%;
    background: rgb(59 59 59 / 0.9);
    position: relative;
    box-sizing: border-box;
    border-bottom: 4px solid rgb(255 255 255 / 0.9);
}
  
 .bottom {
    width: 100%;
    height: 30%;
    background: rgb(59 59 59 / 0.9);
    position: relative;
    box-sizing: border-box;
    border-top: 4px solid rgb(255 255 255);
}
  
  input[type=submit] {
    cursor: pointer;
    background: rgb(0 0 0 / 50%);
    border: 1px solid rgb(50,50,50);
    color: white;
    transition: 0.5s;
}
  
input[type=submit]:hover {
    background: rgb(0 0 0 / 80%);
    border: 1px solid rgb(255 255 255);
    color: rgb(255 255 255);
    border-radius: 20px;
    transition: 0.5s;
}
  
  .cookie{
  	background: #363636;
  }
  
  .accesscode input[type=submit]:hover {
  	    background: rgb(255 255 255);
    border: 1px solid rgb(0 0 0);
    color: rgb(0 0 0);
    border-radius: 0px;
  }
  
  .persubject p {
  	color: white
  }
  
.eachcommu a:hover {
    color: wheat;
  	font-style: italic;
}
  
  #custom-button:hover {
    background: rgb(255 255 255);
    border: 1px solid rgb(0 0 0);
    color: rgb(0 0 0);
    border-radius: 5px 5px 5px 5px;
}
  
  .post input[type=submit]:hover {
    background: rgb(255 255 255);
    border: 1px solid rgb(0 0 0);
    color: rgb(0 0 0);
    border-radius: 20px 20px 20px 20px;
}
  
  .myresources {
  	height: 100%;
  }
  
  .standard input[type=submit]:hover, .indiv a:hover {
    background: rgb(0 0 0 / 80%);
    border: 1px solid rgb(255 255 255);
    color: rgb(255 255 255);
    border-radius: 0px 0px 0px 0px;
}
  
  #quizsub{
  	color: white;
  }

  #qtype{
	color: white;
}

.updatepass input[type=submit]:hover, .forpersonal input[type=submit]:hover, .addquiz input[type=submit]:hover {
	background: rgb(32, 32, 32, 0.80);
    border: 1px solid rgb(255 255 255);
    color: rgb(255 255 255);
    border-radius: 0px 0px 0px 0px;
}
}
