html, body {
  overflow-x: hidden !important;
}
* {
    box-sizing: border-box;
    font-family:'Rubik' !important;
}
.ChatModeTip-container {
    color: hsla(0, 0%, 100%, .6) !important;
}
.nimo-room .nimo-room__activity .nimo-iframe__wrap {
    display: none;
}
.nimo-room .nimo-r_recommend-vertical {
    display: none;
}
.nimo-room .nimo-rm_share {
    display: none;
}
.nimo-room .nimo-room__rank {
    display: none;
}
#side-bar {
    display: none;
}
.nimo-menu-item[data-id=recharge] {
    display: none;
}
.nimo-menu-item[data-id=goLive] {
    display: none;
}
.nimo-menu-item[data-id=app] {
    display: none;
}
.nimo-header-following {
    display: none;
}
.nimo-header-chat {
    display: none;
}
.nimo-room .nimo-anchor-level-badge {
    display: none;
}
.nimo-room .nimo-rm_meta-rank-enter {
    display: none;
}
.MessageList .nimo-scrollbar > div:not(.nimo-room__chatroom__message-item) {
    display: none;
}
.MessageList .nimo-scrollbar {
  width: 98%;
/*   display: flex;
  flex-direction: column;
  justify-content: end; */
}
.nimo-room .float-navigator-container {
    display: none;
}
.nimo-room .chat-room__enter_banner {
    display: none;
}
.nimo-room .top-message-list {
    display: none;
}
.nimo-room .nimo-gift-banner {
    display: none;
}
.nimo-room .nimo-room__chatroom__box-gift {
    display: none;
}
.nimo-room .n-as-theater .nimo-room__gift-shop {
    display: none;
}
.n-as-theater .nimo-room__theater-section {
    max-height: 100vh !important;
}
.nimo-room .nimo-room__nav-tag {
    display: none;
}
.nimo-room .clan-badge {
    display: none;
}
.nimo-room__chatroom__message-item {
    font-size: 14px;
    padding: 5px 0px 4px 10px;
    margin: 0px;
}
.nimo-room .nimo-room__chatroom__message-item__content {
    color: #d3d3d8;
    line-height: 1.3;
}
.nimo-room .nimo-room__chatroom__message-item__info {
    font-weight: 500;
}
.nimo-room .nimo-room__head-img {
    display: none;
}
.nimo-room.has-head-img .nimo-room__main {
    margin-top: 0px;
    padding-bottom: 0px;
    padding-top: 0px;
}
.MessageList .nimo-fans-badge__name {
    display: none;
}
.nimo-room .nimo-cr_decoration_text {
    display: none;
}
.nimo-room .GiftItemMaker {
    display: none
}
.nimo-room__theater-section {
    max-height: calc(100vh - 60px + 96px) !important;
    height: 100%;
}
.nimo-room__main {
    background: #141414;
    padding-bottom: 0px;
    padding-top: 0px;
}
.nimo-room__theater-section>div {
    margin: 0;
}
.nimo-room__theater-section>div:first-child {
    flex-direction: column-reverse;
}
.nimo-room__theater-section>div:nth-child(2) {
    margin-bottom: 96px;
}
.n-as-theater .nimo-room__theater-section>div:nth-child(2) {
    margin-bottom: 0px;
}
.n-as-theater {
  height: 100vh;
}
.nimo-room__player__wrap {
  flex-grow: 1;
}
.nimo-rm {
  justify-self: flex-end;
  margin-top: auto;
}
.nimo-room__main>div:not(:nth-child(3)) {
    margin: 0;
}
.nimo-room__main>div:nth-child(3) {
    margin-top: 2rem;
}
.nimo-room .n-as-conf {
    margin-left: 0px !important;
    margin-right: 0px !important;
    height: calc(100vh + 96px);
}
.nimo-room .marquee-container {
    display: none;
}
.nimo-room .n-as-line-under {
    text-decoration: none !important;
}
.nimo-room .nimo-rm_title-name {
    font-size: 19px;
}
.n-as-theater .nimo-room__gift-shop {
    display: none;
}
.nimo-room .nimo-room__gift-shop {
    order: -1;
    padding: 10px;
    height: 96px;
}
.nimo-room .nimo-rm {
    padding-left: 20px;
}
.nimo-room__gift-shop__gift-list, .nimo-room__gift-shop-left {
    filter:grayscale();
}
.nimo-room__gift-shop__gift-pack-btn {
    background-color: #6c47ff;
    border: 1px solid #6c47ff;
    color: #e2bd84;
    box-shadow: none;
}
.nimo-room__gift-shop__recharge-btn {
    color: #000;
}
.nimo-btn-shop {
    background: #6c47ff;
    color: #FFF;
    z-index: 0;
}
.video-player {
	background: #010001;
}
.nimo-currency {
    margin-left: .5rem;
}
}
