css:
@-moz-document domain("youtube.com") {
/* 
 * Name: Speedsters progressbar
 * Title: Speedsters Progressbar
 * Author: Dino
 * Version: 0.4
 *
 */
/*This is where the speedsters are at*/
 .html5-scrubber-button, .ytp-scrubber-pull-indicator::after {
    background: url("/*[[Speedsters]]*/") ! important;
    background-repeat: no-repeat ! important;
    width: 384px;
    height: 240px;
    border: none;
    margin-left: -180px;
    margin-top: -110px;
    opacity: 1;
    background-size:100%;
    transform: rotate(-50deg) scale(0.3);	
}
.ytp-scrubber-button {
	background-color: inherit;
	color: inherit;
	fill:inherit;
	border-radius: none;
	background: inherit;
	transform: scale(0.6);
	background-size: 100%;
}
/*This is the trail that the speedsters leave behind*/
.html5-play-progress, .ytp-play-progress {
 	background: url("/*[[Trails]]*/") ! important;
}
.html5-load-progress, .ytp-load-progress {
    background-color: /*[[bgC]]*/;
    opacity: 30%;
}
}
