css:
@-moz-document url-prefix('https://www.beyondgaming.be/') {
.block-header {
    border-bottom: 1px solid #981620;
	background: #cccccc;
}
.button.button--cta, a.button.button--cta {
    border-color: #981620 #981620 #981620 #981620;
	background-color: #981620;
}
.node--unread .node-icon i::before {
    color: #981620;
	opacity: 1;
}
.subNodeLink.subNodeLink--unread::before, .subNodeLink.subNodeLink--unread .subNodeLink-icon {
    color: #981620;
}
.badge.badge--highlighted {
    background-color: #981620;
}
.structItem.is-unread .structItem-title a:not(.labelLink)::after {
    background: #981620;
}
.p-body {
    background-color: #b3b3b3;
}
.block-container {
    background: #d9d9d9;
}
.scNormalThreads {
    background: #cccccc;
}
.block-filterBar {
    background: #cccccc;
    border-bottom: 1px solid #981620;
}
.pageNav-page.pageNav-page--current {
    color: #981620;
}
.bbWrapper a {
    color: #981620;
}
.message-newIndicator, a.message-newIndicator {
    background: #981620;
}
.bbCodeBlock {
    border-left: 3px solid #981620;
}
.bbCodeBlock-expandLink a, .message-expandLink a {
    color: #981620;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
    background: #cccccc;
}
.message-cell.message-cell--main {
    background: #d9d9d9;
}
.tabs--standalone .tabs-tab.is-active {
    border-color: #981620;
}
}
