@-moz-document domain("atmarkit.co.jp") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#wrap
, #wrap div
, .outbody
, .outbody div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#wrap *
, .outbody * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#wrap a:link { color : #0000ee !important; }
#wrap a:visited { color : #551a8b !important; }
#wrap a:hover { color : #ee0000 !important; }
#wrap a:hover { background-color : transparent !important; }
/* /destroy div layout */
}

@-moz-document
  domain("www.atmarkit.co.jp")
, domain("jibun.atmarkit.co.jp") {
#gnavi
, #header_r
, #header_ad
, #navibar
, #tabmenu
, #headmenu
, #rightcol
, .sinchaku_ttl
, .sinchaku_cont1
, div[style="font-size: 12px; font-weight: bold;"]
, .btmrss
, .btmlist + br + table[cellspacing="0"][cellpadding="0"][border="0"]
, .sinchaku_cont1 + br + table[cellspacing="0"][cellpadding="0"][border="0"]
, img[src$="indexarrow.gif"]
, img[src$="backn2.gif"]
, #frec
, .fspecial
, #header_l a img
{ display : none !important; }

#header_l a:before
{ content : attr( href ); }
}


@-moz-document domain("monoist.atmarkit.co.jp") {
#header
, #headmenu
, #crec
, #kyujinmono
, #feature
, #ranking
, #monosp
, .b-artbtm
, #sponsorm
, .m-mag
, #frec
{ display : none !important; }

#colM .btmlist h3
, .article #colM #main-contents li
{ margin : 0 !important; }

#colM .btmlist h3
, .btmlist li
, #colR h2
, #newshead ul li
{ padding : 0 !important; }
}
